css:
@-moz-document url-prefix("http://avisynth.nl") {
:root {
	--bg0: #020202;
	--bg1: #1C1C1C;
	--txt0: #c6c6c6;
	--link0: #5ecbff;
}

div#content,
body {
	background: var(--bg0) !important;
	color: #C6C6C6;
}

#bodyContent a.external {
	color: var(--txt0) !important;
}

h1,
h2,
h3,
h4,
h5,
h6 {
	color: #c6c6c6;
	background: var(--bg0) !important;
}

div.noarticletext {
	border: 1px solid #ccc;
	background: var(--bg1);
	padding: .2em 1em;
	color: var(--txt0) !important;
}

#toc,
.toc,
.mw-warning {
	border: 1px solid #aaa;
	background-color: var(--bg1) !important;
	font-size: 95%;
}

table.wikitable {
	background: var(--bg1) !important;
	color: var(--txt0);
}

.wikitable th {
	background: #626262;
}

a {
	font-size: 120%;
	color: var(--link0);
}

a.new,
#quickbar a.new {
	color: var(--link0);
}

.pBody {
	font-size: 95%;
	background-color: var(--bg1);
	color: black;
	border-collapse: collapse;
	border: 1px solid #aaa;
	padding: 0 .8em .3em .5em;
}

code {
	background-color: var(--bg0);
}


#p-cactions li.selected a {
	border-color: #aaa;
	background-color: var(--bg1);
}
#p-cactions li.selected {
	border-color: #aaa;
}

#p-cactions li a {
	background-color: var(--bg1) !important;
	color: var(--link0) !important;
}

pre {
	border: 1px dashed #2f6fab;
	color: #ddd;
	background-color: #4f4f4f;
	font-size: 110%;
}

.catlinks {
	border: 1px solid #aaa;
	background-color: var(--bg0);
}

div#footer {
	background-color: var(--bg0);
	border-top: 1px solid #aaa;
	border-bottom: 1px solid #aaa;
}
}
