@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {
/**** Global ****/
* {font-family: tahoma !important; line-height: 1.5em !important; font-size: 98% !important}
body {background: #E3E3E3 none !important}

/**** Header ****/
#logo a img {display:none !important}
#logo a {width: 234px !important;height: 53px !important; display: block !important; background: url("http://i.friendfeed.com/c8395dd85dbdcee3d942e189c592994feac69f57") no-repeat !important}
#searchbox {direction: rtl !important}
textarea.title {direction: rtl !important}

/**** Entry ****/
.entry {direction: rtl !important; border: 2px solid #93BAEB !important; -moz-border-radius: 5px !important; margin-bottom: 10px !important; padding: 10px !important}
.entry .profile {float: right !important; margin-left: 10px !important}
.entry .body {margin-left: 0 !important; margin-right: 63px !important}
.profile .entry .body {margin-left: 0 !important; margin-right: 0 !important}
.media {overflow: visible !important}
.entry .likes {background-position: right top !important}
.entry.private .info {padding-left: 0 !important; padding-right:14px !important;}
.entry.private .info, .entry.direct .info {background-position:right center !important;}
.entry .media img {border: 1px solid #93BAEB !important}
.entry .media a:hover img {border: 1px solid #2F72C4 !important}
a.l_expandmedia {display: block !important; width: 18px !important; height: 18px !important; margin-right: 5px; background: transparent url("http://i.friendfeed.com/bea00b42369ae9732e0d2e11772d93dc1e0bba7f") no-repeat !important;}
a.l_expandmedia img {display: none}
.foaf:before {content: " Ùˆ " !important}

