/**** Comments and Likes ****/
.entry .comment .quote {float:right !important;}
.entry .comment .content {margin-left:0 !important; margin-right:21px !important}
.comment {font-size: 85% !important; background-position: right 4px !important; padding: 3px !important; border: 1px solid #F6F6F6 !important; -moz-border-radius: 5px !important}
.comment.friend {background-color: #F6F6F6 !important; border-color: #DFDFDF !important}
.comment.owner {background-color: #FFFBEF !important; border-color: #F0EACB !important}
.commentform {background-position: right 3px !important}
.likes, .commentform {padding-left:0 !important;padding-right:21px !important;}
.likes, .info {font-size: 85% !important}
.info {margin-top: 10px !important}
a.date:after {content: " ÙØ±Ø³ØªØ§Ø¯Ù‡ Ø´Ø¯Ù‡" !important}
.likes {direction: ltr !important; text-align: right !important}
.l_editcomment:before {content: 'ÙˆÙŠØ±Ø§ÙŠØ´ ' !important}
