.via {font-size: 80% !important}
.comment .quote:before {content:attr(title) !important; display: none !important; direction: ltr !important}
.comment .quote:hover::before {display: block !important; width: 80px !important; text-align: center !important; background: #ccc !important; border: 1px solid #333 !important; -moz-border-radius: 3px !important; padding: 5px !important}
.comment .quote:hover{z-index:10 !important; position:relative !important}
.entry .comment.deleted, .entry .comment.friend, .entry .comment.owner, .entry .commentform {background-image: url("http://i.friendfeed.com/26afc7fb31e3c4956704421fe03fcf2a9d60f8ce") !important}

/**** Sidebar ****/
.box-bar {background-image: url("http://friendfeed.com/static/images/bar-home.png") !important; border-color: #2F72C4 !important}
.box-bar .box-corner {background-image: url("http://friendfeed.com/static/images/bar-home.png") !important}
.box .box-bar .box-bar-text {height: 28px !important}
#sidebar .box .box-body {background-color: #fff !important}
#sidebar .box .box-bottom {background: url("http://i.friendfeed.com/5fdc449c0efdb64e78ba1e093ef1e5c110c437ff") -10px bottom no-repeat !important}
#sidebar .box-bottom .box-corner {background-image: url("http://i.friendfeed.com/5fdc449c0efdb64e78ba1e093ef1e5c110c437ff") !important}
.box li a[href*=direct].unread {color: red !important; text-decoration: blink !important}
