css:
@-moz-document url-prefix("https://www.anibox.org/") {
*:not(.fa) {
    font-family: Roboto !important;
}
* {
    border: none !important;
    border-radius: 0 !important;
    text-shadow: none !important;
}
#right,
#left,
#allEntries,
.commall,
.center,
.perek,
.header,
#footer,
.aboutmat,
.header-menu > ul:nth-child(2) > li:nth-child(3) > a:nth-child(1),
.header-menu > ul:nth-child(2) > li:nth-child(1) > a:nth-child(1),
.header-link > ul:nth-child(1) > li:nth-child(2) > a:nth-child(1),
.h-login {
    display: none;
}
#page,
.body-izb {
    background-image: none;
    background-color: #494949;
}
p.text:nth-child(6),
span14,
.justify {
    padding: 0.5rem 1rem;
    font-size: 1em;
    color: #E5DFC5;
    text-align: justify;
}
#content {
    background-color: #252120;
    width: 80% !important;
    margin-left: 10% !important;
}
#content > article:nth-child(1) {
    padding: 0 10px;
}
#header {
    height: 60px;
    font-size: 1.3rem;
    overflow: hidden;
    background-color: #252120;
}
#header a {
    color: #91C7A9;
}
.header-link {
    margin-left: 10%;
}
.header-link a,
.header-menu a {
    padding: 15px 20px 12px;
    font-size: 1.3rem;
}
.h-menu {
    top: -1rem;
    left: -8.5%;
}
h1 {
    font-size: 2rem;
    line-height: 1.2;
    color: #AA2F41;
    margin-top: 0.5rem;
}
#drnaz {
    color: #AA2F41;
    padding: 0.5rem 0;
    font-size: 1.2rem;
}
#content {
    width: 100%;
    margin-top: 1rem;
    margin-left: 0;
}
.anim_links > a.spoiler_links {
    padding: 5px;
    background-color: #1B1716;
    margin: 0 0 1rem 0;
}
.anim_links > .spoiler_body {
    background-color: #1B1716;
    margin: -0.5rem 0 1rem 0;
    color: #E5DFC5;
}
a {
    color: #AA2F41 !important;
}
a:hover {
    color: #B48375 !important;
}
.active-area {
    height: 100%;
}

#pipipipi i,
a.zapomn::before {
    color: #AA2F41 !important;
}
#anime-online,
#pipipipi {
    background-color: #1B1716;
    color: #E5DFC5;
}
#anime-online > div > a {
    background-color: #252120;
    color: #E5DFC5;
}
#anime-online > div > a:hover {
    background-color: #1B1716 !important;
}
#anime-online > div > a::before {
    color: #AA2F41;
}
#anime-online > div > a:hover:before {
    color: #B48375;
}
a.zapomn:hover:before,
a#kinoteatr::before {
    color: #B48375!important
}
.ep1 {
    background-color: #1B1716;
}
.ep2 {
    background-color: #252120;
}
.ep1:hover {
    background-color: #1B1716;
}
.ep2:hover {
    background-color: #252120;
}
c1 {
    color: #AA2F41;
}
c1:hover {
    color: #B48375;
}
.pohojie {
    color: #E5DFC5;
    padding-top: 0.5em;
    margin-bottom: 10px;
    background-color: #1B1716;
}
.pohojie fieldset {
    background-color: #1B1716;
}
#foranime {
    background-image: none;
}
#anime-online > div > div > a {
    background-color: #1B1716;
}
#anime-online > div > div > a:hover {
    background-color: #252120;
}
#anime-online .eps,
#anime-online .eps:hover {
    background-color: #252120 !important;
    color: #E5DFC5 !important;
}
#text {
    display: none;
}

.ongoing-main li:nth-child(odd) > a,
.ongoing-main li:nth-child(even) a:hover .ongoing-data > span {
    background-color: #1B1716
}
.ongoing-main li:nth-child(even) > a,
.ongoing-main li:nth-child(odd) a:hover .ongoing-data > span {
    background-color: #252120
}
.bar-desc {
    top: 50%;
    margin-left: 5px;
}
.logo {
    padding: 0.5rem;
    display: block;
    margin: 0 auto;
}
#screens {
    padding: 0 150px;
    padding-top: 5px;
    padding-bottom: 5px;
}
.left-controlbar span,
#scroll-back span {
    font-size: 64px;
    max-width: 10px;
    background-color: rgba(0, 0, 0, 0) !important;
}
.leftbar-wrap .active-area:hover,
#scroll-back,
.left-controlbar {
    background-color: rgba(0, 0, 0, 0) !important;
    height: 100%;
}
}

@-moz-document url-prefix("https://www.anibox.org/search/") {
input[type="text"],
input[type="password"],
input[type="text"]:hover,
input[type="password"]:hover {
    color: #E5DFC5;
    background-color: #1B1716;
    background-image: none;
}
.header-menu > ul:nth-child(2) > a:nth-child(3) {
    display: none;
}
#poa1 {
    background-color: #252120;
    color: #E5DFC5;
}
/* .inf_p, .inf_p div, .inf_p p, .inf_p td { */
.inf_p,
.inf_p td,
.inf_text {
    color: #E5DFC5 !important;
    background-color: #1B1716;
}
.inf_text {
    padding: 0.4rem;
}
.inf_a > p {
    margin-top: -50px !important;
    color: #E5DFC5;
}
input[type="button"],
input[type="submit"],
input[type="reset"],
.button > a,
.hersh a,
input[type="submit"]:hover {
    background-color: #1B1716;
    background-image: none;
    color: #AA2F41;
}
input[type="submit"]:hover {
    color: #B48375;
}
}
