css:
@-moz-document domain("tvgry.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #9B0201 #3b3b3b;
}
input[type="text"],input[type="email"],input[type="password"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #9B0201 !important;
}
*{
    box-shadow: none !important;
}
/*HEADER*/
.mnu-main-p{
    background: #1c1c1c;
}
.mnu-main-p-mnu a,.mnu-main-p-mnu2 a,.mnu-main-p-watek1 b{
    color: #9B0201;
}
.mnu-main-p-news a,.mnu-main-p-tem a, .mnu-main-p-tem p,.mnu-main-p-pla a,.mnu-main-p-ht2 p b,.mnu-main-p-ht2 p,.mnu-main-p-ddl a, .mnu-main-p-ddl p,.mnu-main-p-s-tit,.mnu-main-p-g-tit,.mnu-main-p-5for a, .mnu-main-p-5for p,.mnu-main-p-watek2{
    color: #fff;
}
/*HEADER*/
/*STRONA-GŁÓWNA-TV*/
.mnu-third,main{
    background: #1c1c1c;
}
.mnu-third a,.tvgry-main-top .tv-box-short b{
    color: #9B0201;
}
.mnu-third a:hover{
    background: #9B0201;
}
.tvgry-skin h1,.tvgry-main-top .tv-box-short,.download-main .search17-h3, .search17-h3,.tvgry-lista-main .tv-box:hover .tv-box-title,.tvgry-main-lista-trailers-menu h3,.tvgry-lista-wide .tv-box:hover .tv-box-title,.tvgry-main-info p{
    color: #fff;
}
.tvgry-main-top .tv-box-title,.tvgry-lista-main .tv-box-title,.tvgry-lista-wide .tv-box-title{
    color: #e44600;
}
.search17-tvgry{
    background: #1c1c1c;
    border: 1px solid #9B0201;
}
.search17-box,.search17-box:hover,.search17-top-c .search17-box{
    border: none;
    background: #1c1c1c;
}
/*STRONA-GŁÓWNA-TV*/
/*FILM*/
.tvgry-player-pasek,.kom-lay-2016-c{
    background: #1c1c1c;
}
.tvgry-player-opis,.tvgry-koncowa-opis-filmu-txt{
    color: #fff;
}
.tvgry-lista-wide h4{
    color: #e44600;
}
.tvgry-koncowa-opis-filmu-lead{
    color: #9B0201;
}
/*FILM*/
/*PODSTRONY*/
/*PODSTRONY*/
/*FOOTER*/
.footer-top{
    border: 0;
}
.footer-main,.footer-sites,.footer-bottom{
    background: #1c1c1c;
}
.footer-copyr:after{
    content: " | Ciemny motyw by Maciejozaman";
    color: #e44600;
    cursor: none;
}
/*FOOTER*/
}
