css:
@-moz-document url-prefix("https://www.nvidia.com/en-us/geforce/forums/") {
.content2__body, .site__content {
    background-color: #101010;
}

.comment2__title {
    background-color: #1a1a1a;
    color: #ccc;
    border-color: #444;
}

.comment2__body, .reply-strip, .reply-strip__textarea {
    background-color: #1a1a1a;
    color: #ccc;
}

.reply-strip {
    border-color: #444;
}

.comment2__info, .comment2__text, .comment2__author {
    color: #ccc;
}

.nvidia {
    background-color: #101010;
    color: #ccc;
}

.nvidia a svg > path:nth-child(3) {
    fill: #76b900 !important;
}

.site__nav {
    display: none;
}

.sidebar-columns__right {
    display: none;
}

.site .site__sidebar {
    width: auto;
    max-width: 50%;
}

/* Remove this selector if you prefer the truncated topic title bar */
.crown__content__title {
    background-color: #1a1a1a;
}

*::-webkit-scrollbar {
    width: 12px;
    background-color: #101010;
}

*::-webkit-scrollbar-track {
    box-shadow: inset 0 0 6px rgba(10, 10, 10, 0.3);
}

*::-webkit-scrollbar-thumb {
    border-radius: 10px;
    -webkit-box-shadow: inset 0 0 4px rgba(0, 0, 0, .3);
    background-color: #312f3c;
}

*::-webkit-scrollbar-corner {
    background: rgba(0, 0, 0, 0.1);
}



}
