css:
@-moz-document domain(culearn.carleton.ca){
*{  
    color:#eee;  
}
#page, .card-body {
    background-color:#222;
}
#region-main, .region_main_settings_menu_proxy, .form-inline, .path-backup .backup-section, .path-backup .mform .root_setting:nth-of-type(2n),.path-backup .mform .grouped_settings:nth-of-type(2n){
    background-color:#333;
}

#nav-drawer, .dropdown-menu, .dropdown-item, .forumpost.unread .row.header {
    background-color:#222;
   
}
.dropdown-item a{
    color:#ccc;
}

.dropdown-menu{
    border:1px solid white;
}

.custom-select:disabled{
   background-color:#000;
    color:#222;
}

.list-group-item, .custom-select{
    background-color:#333;
    color:#fff;
}
.list-group-item:hover, .dropdown-item:hover, .dropdown-item:focus{
    background-color:#5cc;
}
.list-group .list-group-item.active{
    background-color:#255;
    border:none;
}

.filemanager, .form-autocomplete-suggestions *{
    color:#000;
}

.moodle-dialogue-base .moodle-dialogue-wrap.moodle-dialogue-content{
    background-color:#000;
    color:#ccc
}
.moodle-dialogue-base .moodle-dialogue-wrap .moodle-dialogue-hd.yui3-widget-hd{
    color:#ccc;
}

.popover *, .gradeparent *, .editor_atto_notification, .editor_atto_content p {
    color:#000;
}

.modal-content{
    background-color:#444;
}

/*Tables*/
.path-mod-assign .gradingtable tr.unselectedrow td{
    background-color:#333;
}
.path-mod-assign .gradingtable tr.selectedrow td{
    background-color:#033
}
.path-mod-assign div.submissionstatussubmitted{
    background-color:#4a4
}
}
