css:
@-moz-document url-prefix("https://www.autohotkey.com/docs/"), url-prefix("https://lexikos.github.io/v"), url-prefix("https://hotkeyit.github.io/v") {
/* Autohotkey_L & Autohotkey_H */

body {
	font-family: sans-serif;
	font-size: initial !important;
}

/* 

text */

.methodShort {
	border: solid 1px #0002;
}

.methodShort:target {
	border: solid 1px #0009;
}

/* 

tables */
table.Info,
table.info {
  border: none;
}

table.Info th,
table.info th {
  border: solid 1px #0002;
  overflow-wrap: break-word;
  text-align: left;
}

table.Info td,
table.info td {
  border: solid 1px #0002;
  overflow-wrap: break-word;
  vertical-align: top;
  _word-break: break-all;
}

/* 

footer */
#right .back-to-top {
  border-radius: 50%;
  background: #0009;
  display: block !important;
  width: 40px;
  height: 20px;
  font-family: FontAwesome;
}

#right .back-to-top::before {
  content: '\f077';
  font-size: 25px;
  position: relative;
  top: -2px !important;
}

@font-face{
  font-family:'FontAwesome';
  src:url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.9.0/webfonts/fa-solid-900.ttf');
}
}

@-moz-document url-prefix("https://www.autohotkey.com/docs/"), url-prefix("https://lexikos.github.io/v") {
/* Autohotkey_L */

/* 

header */
#head {
  box-shadow: none; 
  height: 35px;
}

.h-area {
  padding-left: 30px;
}

#head .h-tabs li {
  font-size: 14px;
  height: 35px;
  line-height: 34px;
  display: inline-block;
  padding: 0 15px;
  margin: 0;
}

#head .h-tabs li:not(.selected)+li:not(.selected) {
  padding-left: 15px;
}

#head .h-tabs .selected {
  background: white;
}

#head li {
  line-height: 35px;
}

#head .h-tabs .selected:hover {
  background: white;
  color: #3f84bd;
}

#head li:hover {
  color: white;
  background: #3f84bd;
  height: 35px;
}

#head .dropdown {
  width: 35px !important;
  height: 30px;
}

#head .dropdown li {
  line-height: 25px;
}

#head .h-tools {
  font-size: 21px;  
  height: 35px;
}

/* 

text */
h1 {
  color: #0009;
  margin-top: 20px;
  font-size: 1.8rem;
  font-family: Roboto;
  font-weight: 300;
}

#right p, 
#right li,
.info td,
_ {
  font-family: Georgia !important;
  font-size: 1.5rem !important;
  line-height: 1.4 !important;
}

dd {
  border-left: solid 1px #0002;
}

/* 

code */
pre {
  font-size: 1.2rem;
  margin: 5px 0;
  border: solid 1px #0002;
  padding: 4px 5px 2px 5px;
  line-height: 1.4;
}

#right pre.parent > div.buttons > a,
#right pre.parent > div.buttons > a:hover,
_ {
  background: #0004;
}

.origin, code {
  font-family: "PragmataPro Mono" !important;
}

/* 

sidebar */
#left {
  width: 231px !important;	
}

#left .toc {
  padding-left: 9px; 
  font-family: Roboto;
}

#left .toc ul {
  color: #000d;
  padding: 2px 0;
}

#left .toc li > span {
  font-size: .8rem;
  line-height: 1.1;
  padding-left: 7px;
  padding-top: 0;
}

#left .toc ul > li > ul {
  margin-left: 12px;  
}

#left .toc .selected {
  border-left: 2px solid #d22;
  color: #d22;
}

#left .toc ul > li.highlighted > ul {
  border-left: solid 1px #0003;
}

#left .toc li.closed > span::before {
  z-index: 999;
}

#main li {
  line-height: 1.2;
}

#left, .dragbar {
  background: none;
  border: none;
}

#left .input input {
  border: none;
  border-bottom: 1px dotted #000d;
}

#left .list {
  border: none;
}

#left .list > a {
  padding: 1px 0 2px 0;
  font-size: .8rem;
}

#left .select select {
  border: none;
  border-bottom: 1px dotted #000d;
}

#left .search .checkbox label {
  border: none;
  top: 1px;
}

/* 

footer */
#right .footer {
  border: none;
  font-size: 9px;
  padding: 0;
  margin: 0;
}
}

@-moz-document url-prefix("https://hotkeyit.github.io/v") {
/* Autohotkey_H */

#main-content,
.hdr-table {
  max-width: 100%;
}

.header,
#headerbar,
.nav,
.nav li span,
.right-col #main-content,
.footer,
_ {
  color: black;
  background: none;
  border: none;
}

table {
  margin-bottom: 0;
}

/* 

header */
.hdr-image {
  display: none;
}

.header {
  background: #8b3;
  position: fixed;
  right: 0;
  top: 0;
  padding: 0;
  height: 35px;
  width: calc(100% - 231px);
}

.hdr-table .hdr-search,
.hdr-table .hdr-language {
  margin: 0;
  padding: 0;
  display: inline-block;
  line-height: 29px;
}

#q {
  border: none;
  background: none;
  color: white !important;
  border-bottom: 1px dotted white;
  height: 20px;
  line-height: 20px;
  margin: 0;
}

#search-btn {
  text-transform: none !important;
  background: none !important;
  border: none !important;
  color: white !important;
  font-weight: bold;
  width: auto !important;
}

.hdr-table .hdr-language>ul>li,
.hdr-table .hdr-language>ul>.selected {
  position: absolute !important;
  top: 2px !important;
  right: 25px !important; 
}

.hdr-language li {
  text-transform: none !important;
  background: none !important;
  border: none !important;
  color: #fff5 !important;
  font-weight: bold;
}

.hdr-table .hdr-language .second {
  background: #8b3;
}

.hdr-table .hdr-language .second .selected {
  background: #ad2 !important;
  color: white !important;
}

/* 

header sidebar */
.left-col {
  position: absolute;
  top: 0;
}

.nav {
  position: fixed;
  z-index: 999;
  margin-bottom: 10px;
  background: #8b3;
  height: 37px;
}

.nav li:first-child {
  margin-left: 32px !important;
}

.nav li {
  margin: 0;
  padding: 0 15px;
  width: auto;
}

.nav li:hover {
  background: #ad2;
}

.nav li.selected {
  background: white;
  color: #8b3;
}

.nav li.selected:hover {
  background: white;
}

.nav li span {
  color: white;
  display: inline !important;
  font-size: 14px;
  font-weight: bold; 
  line-height: 37px;
  padding: 0;
  text-transform: none;
}

.nav li.selected span {
  color: inherit;
}

.nav li span:hover {
  background: none;
  color: white;
}

.nav li.selected span:hover {
  color: #ad2;
}

/* 

sidebar */
#keywords {
  position: fixed;
  top: 45px !important;
}

#sidebar {
  margin-top: 55px;
}

#keywords input {
  border: none;
  border-bottom: 1px dotted #000d;
  margin-bottom: 5px;
}

#IndexEntry {
  width: 235px !important;
  height: 20px;
  display: block;
  border-bottom: 1px dotted #0009 !important;
}

#keywords select,
#sidebar li {
  padding-top: 0;
  border: none;
  width: 235px; 
  font-family: Roboto;
  font-size: .7rem;
  line-height: 1.25;
}

ul.jqtree-tree .jqtree-element {  
  width: 200px !important;  
}

ul.jqtree-tree,
ul.jqtree-tree ul.jqtree_common {
  margin-left: 10px;  
}

ul.jqtree-tree .jqtree-toggler {
  left: -12px;
  margin-left: 0;
  top: 8%;
}

.jqtree_common {
  font-family: Roboto;
}

/* 

text */
#main-content {
  padding-left: 10px;
}
#bodyContent,
#main-content p, 
#main-content li, 
.info td,
.Info td,
_ {
  font-family: Georgia;
  font-size: 1.3rem;
  line-height: 1.4;
}

#bodyContent h1,
#bodyContent h2,
#bodyContent h3,
#bodyContent h4,
#bodyContent h5,
#bodyContent h6,
_ {
  font-family: sans-serif;
  font-size: initial;
}

.navh1 {
  background: none;
  border: none;
  color: #0009;
  font-size: 1.5rem;
  font-family: Roboto;
  font-weight: 300;
  margin-top: 55px;
}

.navh1 a {
  color: #0006 !important;
}

h1, h2, h3 {
  height: 41px !important;
}

dt {
  margin-left: 0px;
}
dd {
  border-left: solid 1px #0002;
  margin-left: 4px;
}

.indented {
  text-indent: 0; 
}

hr {
  height: 1px;
  border: none;
  border-top: solid 1px #0002;
}

/* 

code */
pre, code {
  font-family: "PragmataPro Mono";
  font-size: 1rem;
  line-height: 1.4;
  border: solid 1px #0002;
  margin: 5px 0;
  padding: 4px 5px 2px 5px;
}

pre.Syntax,
pre.Syntax p,
_ {
  padding: 5px 10px;
  font-family: "PragmataPro Mono" !important;
  font-size: 1rem !important;
  line-height: 1.4;
  margin: 0;
}

pre.Syntax p {
  padding: 0;
}

div[style*="margin-top: -28px"],
div[style*="margin-top: -32px"],
_ {
  background: white;
  font-family: "PragmataPro Mono";
  line-height: 10px;
  margin: 0 !important;
  margin-top: -25px !important;
}

div[style*="margin-top: -28px"] {
  top: -25px;
  padding: 9px 0 0 9px;
  border-left: solid 1px #0002;
  border-top: solid 1px #0002;
}

div[style*="margin-top: -32px"] {
  margin-top: 5px !important;
}

pre.Syntax div[style*="margin-top: -28px"] {
  background: #ffa;
}

/* 

footer */
.footer, .footer * {
  font-size: 9px;
  color: #0005 !important;
}
}

@-moz-document url-prefix("https://www.autohotkey.com/v2/v2-changes.htm") {
/* Autohotkey changelog */
p, li {
	font-family: Georgia;
	font-size: 1.4rem;
	line-height: 1.5;
	border: none;
	padding: 0;
}

code {
	font-family: "PragmataPro Mono";
	font-size: 1.2rem;
	line-height: 1.4;
}
}

@-moz-document url-prefix("https://www.autohotkey.com/boards") {
/* Autohotkey forums */

body, 
htmnl,
.bg1,
.bg2,
.navbar,
.navbar .nav-tabs .tab.selected,
_ {
	background: white;
}

br+br {
  _display: none;
}

blockquote,
.postbody .content,
_ {
	font-family: Georgia !important;
	font-size: 1.4rem;
	line-height: 1.4;	
	padding: 0;
}

blockquote cite {
	margin: 0;
}

blockquote.uncited {
	padding: 0;
  padding-left: 1rem;
}

blockquote {
  border: none;
	border-left: 1px solid #0002;
	margin: 10px 0;
	padding: 0;
  padding-left: 1rem;
}

blockquote::before {
	display: none;
}

.inline_code {
	border: none;
	font-family: PragmataPro;
	font-size: 1rem;
}

.postprofile {
	padding-top: 2rem;
} 

.author,
.notice,
.post-buttons {
	opacity: .4;
}

.author:hover,
.notice:hover,
.post-buttons:hover {
	opacity: 1;
}

code[class*="language-"], pre[class*="language-"] {
	font-family: PragmataPro;
	font-size: 0.9rem;
}

.codebox {
	border: none;
	border-left: 1px solid #0002;
	margin: 0;
}

.codebox p,
.postbody .content,
.postprofile,
.signature,
_ {
	border: none;
}
}

@-moz-document domain("forum.script-coding.com") {
/* Autohotkey forum.script-coding.com */

:root {
	--c: #0002;
}

.main-content,
.main-subhead,
.post,
.postbody,
.posthead,
.posthead .hn,
.post-options,
.rep_plus_minus,
#brd-head,
_ {
	background: none;
}

.fancy_spoiler_switcher,
.gen-content, .main-content,
.posthead .hn,
.posthead,
.postbody,
.postfoot,
.postfoot .post-options,
.main-subhead,
.main-item div,
.main-item .item-subject,
.main-content .main-item,
_ {
	border: none;
}

.post {
  border-bottom: 1px solid #0001;	
}

.sig-line,
_ {
	display: none !important;
}

.entry-content,
.fancy_spoiler_switcher,
_ {
	margin: 0;
	padding: 0;
}

/* 

message */
.entry-content p {
	font-family: Georgia;
	font-size: 1.3rem;
	line-height: 1.4;
}

.post {
	margin-top: 20px !important;
}

.post-link a,
.post-actions a,
.post-num,
.post-ident .post-edit,
_ {
	color: var(--c);
}

.post-link a:hover,
.post-actions a:hover,
.post-num:hover,
.post-ident .post-edit:hover,
_ {
	color: black;
}

/* 

code */
.codebox div:first-child,
.codebox div:first-child a,
_ {
	border: none !important;
	margin-bottom: 10px;
	font-family: sans-serif !important;
	font-size: .7rem;
	margin-right: 5px;
}

.entry-content .codebox {
	border: none;
	margin: 10px 0;
	border-left: 1px solid var(--c);
	padding: 0 0 0 10px;
}

.codetitle,
samp,
code,
pre,
_ {
	font-family: "PragmataPro Mono";
	font-size: .9rem;
}

/* 

quotes */
.entry-content .quotebox {
	margin: 5px 0;
	padding: 0 0 0 10px;
	border: none;
	border-left: 1px solid var(--c);
}


/* 

signature */
.rep_plus_minus {
	padding: 0 !important;
}

.sig-content {
	padding-top: 5px;
	font-family: sans-serif;
	font-size: 10px;
	line-height: 1.3;
}
}
