css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("friendfeed.com") {
body {
font-family:tahoma !important;
font-size:12px !important;
line-height:18px !important;
}
a {
color:#2582AD !important;
}
a:hover {
color:#D54E21 !important;
}

/*---------- TOP ------------*/
input.textbox {
text-align:right;
font-family:tahoma !important;
}
.subbar.home, .subbar.blue {
background:#E4F2FD !important;
}
.sharebox .title textarea {
font-family:tahoma !important;
font-size:12px !important;
text-align:right !important;;
direction:rtl !important;
height:60px !important;
}
.profile .subscribebar a, .subview .subscribebar a, .profile .subscribebar .g, .subview .subscribebar .g {
color:#fff !important;
}
/*--------- ENTRY ------------*/
.entry {
text-align:right !important;
direction:rtl !important;
}
.entry .title {
margin-right:5em !important;
}
.entry .info {
direction:ltr !important;
font-size:11px !important;
margin-right:4.5em !important;
margin-top:10px !important;
}
.entry.private .info {
background-position:right !important;
padding-right:15px !important;
font-size:11px !important;
}
.entry .likes {
background-position:right !important;
font-size:11px !important;
padding-right:20px !important;
direction:ltr !important;
}
.entry .comment.friend {
background-position:right !important;
background-color:#f6f6f6 !important;
border:1px solid #e8e8e8 !important;
font-size:11px;
padding:5px !important;
padding-right:20px !important;
direction:rtl !important;
-moz-border-radius:5px;
}
.entry .comment.owner {
background-position:right !important;
background-color:#FFFCEF !important;
border:1px solid #EFE4B3 !important;
font-size:11px;
padding:5px !important;
padding-right:20px !important;
direction:rtl !important;
}
.entry .comment {
background-position:right !important;
font-size:11px;
padding:10px !important;
padding-right:20px !important;
}
.entry .commentform {
background-position:right !important;
padding-right:20px !important;
}
.content .l_editcomment:before {
content: 'ÙˆÛŒØ±Ø§ÛŒØ´ - ';
}
.content .l_deletecomment:before {
content: 'Ø­Ø°Ù - ';
}
.entry .profile {
float:right !important;
}
.entry .commentform textarea {
font-family:tahoma !important;
font-size:11px !important;
}
.entry .info a.service {
background:#F1FFCF;
border:1px solid #D9EFA4;
font-size:11px !important;
padding:3px;
-moz-border-radius:5px;
}
.entry .info a.service:hover {
background:#F1FFCF;
border:1px solid #B5CF74;
font-size:11px !important;
padding:3px;
-moz-border-radius:5px;
text-decoration:none !important;
}
.sharebox, .entry, .pager.top {
border-bottom: 1px dashed #b0b0b0 !important;
}
/*---------- SIDEBAR -----------*/
#sidebar .box {
text-align:right;
}
.bar a {
color:#fff !important;
}
.bar groups .l_choosefavorites {
text-align:left !important;
float: left !important;
}
.bar.home, .bar.blue, .bar.friends, .bar.green, .bar.groups, .bar.orange, .bar.search, .bar.filters, .bar.approveemail, .bar.red  {
background:#247FA9 !important;
border:0 !important;
}
#sidebar .box .main {
background:#fff !important;
}
#sidebar .box .bottom {
background:#fff !important;
-moz-border-radius-bottomright:10px;
-moz-border-radius-bottomleft:10px;
}
}
