css:
@-moz-document domain("elearning.tgm.ac.at") {
/*scrollbar*/
::-webkit-scrollbar,::-webkit-scrollbar-track-piece{
    background-color:#151515 !important}
::-webkit-scrollbar-corner,::-webkit-scrollbar-thumb{
    background-color:#333 !important;
    border-radius:0.4em;}
* {
    outline-color: #26acd9;
}
/*navbar*/
.navbar{background-color:#222;}
    
    /*dropdown-menu*/
    .dropdown-menu{
        background-color:#222;} /* background color changed by Philip Damianik */
    .dropdown-divider{
        border-top: 0px solid #0000;} /* border removed by Philip Damianik */
    .dropdown-item,.dropdown-item:focus{
        background-color:#0000;}
    .dropdown-item:hover{
        background-color:#444;}
    /*notification drawer*/
    #nav-notification-popover-container>.popover-region-container{
        background-color:#222;
        border-width: 0px;
        box-shadow: #151515 0px -5px 10px 2.5px;}
    #nav-notification-popover-container .notification{
        background-color:#0000 !important;}
    #nav-notification-popover-container .popover-region-header-container,
    #nav-notification-popover-container .popover-region-content-container,
    #nav-notification-popover-container .notification{
        outline: none;
        margin-top: .25rem;
        margin-left: .2rem;}
    /* Following added by Philip Damianik */
    #nav-notification-popover-container div[tabindex="0"]:focus {
        /*outline: none;*/
        color: #66b3cc;
    }
    .content-item-container:hover .content-item-footer .timestamp{
        color:#AAA}
    #nav-notification-popover-container .see-all-link>div{
        background-color:#222;
        border-width: 0px;}
    .popover-region-toggle::before,.popover-region-toggle::after{
        display:none}
    /*message drawer*/
    .message-app{
        background-color:#222 !important}
    .message-app .input-group>input,.input-group-text{
        background-color:#151515 !important;
        border-color:#333; /* color changed by Philip Damianik */
        color:#888;}
    .message-app .form-control:focus{
        color:#AAA;}
    .message-app .card{
        background-color:#222;}
    .message-app .border-bottom{
        border-color:#888 !important;
        border-width: 0px!important;}
    .message-app .input-group .btn{
        border-color:#222;}
    .message-app .card.expanded .list-group .list-group-item{
        background-color:#222;}
    .message-app>.header-container>.bg-white{
        background-color:#222 !important;}
    /* following added by Philip Damianik */
    .message-app .message {
        outline: none;
    }
    .message-app .message.send{
        background-color:#222 !important;}
    .message-app .message.received{
        background-color:#222 !important;}
    .message-app .message.received .tail{
        border-bottom-color:#222;}
    .message-app .message.send .tail{
        border-bottom-color:#222;}
    .message-app .list-group .list-group-item{
        background:#333;}
    .message-app .nav-pills{
        border:solid #888 1px;}
    .message-app .nav-pills>.nav-item>a:not(.active){
        background-color:#333;}
    .message-app .nav-pills>.nav-item>a.active{
        background-color:#444;}
    .message-app>.footer-container>.bg-white{
        background-color:#151515 !important;
        border-color:#888 !important;}
    #conversation-actions-menu-button{
        color:#888;}
    #conversation-actions-menu-button:hover{
        background-color:#555;}
    #conversation-actions-menu-button:focus{
        background-color:#666;}
/*body*/
:root,body,#page{
    background-color:#151515 !important;}
/*footer*/
#page-footer{
    background-color:#151515 !important;
}
/*sideblocks*/
.block.card{
    background-color:#222;}
/*Kurse*/
.dashboard-card{
    border-color:#333;}
.course-info-container{
    background-color:#333;}
.card-img{
    filter:invert(1) hue-rotate(180deg);}
.img-fluid{
    filter:invert(1) hue-rotate(180deg);}
.card-footer{
    background-color:#151515 !important;} /* color in this line edited by Philip Damianik */
.progress-bar{
    background-color:#1f8aad;} /* blue colors changed by Philip Damianik */
.coursemenubtn:hover,.coursemenubtn:focus{
    background-color:#444 !important;}
.course-listitem{
    background-color:#333;}
.course-summaryitem{
    background-color:#151515 !important;
    border: 1px solid #333 !important;}

/*text*/
span,p,.text-center,h1,h2,h3,h4,h5,h6,.text-right,body{
    color:#AAA;}
textarea, textarea.form-control{
    background-color:#222 !important;
    border:solid #222 1px;
    outline: none;
    padding: 6px 12px;
    border-radius:0.25em;
    resize: none;
}
/*links*/
a{
    color:#26acd9 !important;} /* blue colors changed by Philip Damianik */
a:hover{
    color:#66b3cc !important; /* blue colors changed by Philip Damianik */
     text-decoration: none!important;}
/*icons*/
.logo>img{
    filter:invert(1) hue-rotate(180deg);}
.fa-bell{
    color:#26acd9;} /* blue colors changed by Philip Damianik */
.popover-region:hover>.nav-link>.fa-bell,.fa-bell:hover{
    color:#66b3cc;} /* blue colors changed by Philip Damianik */
.empty-placeholder-image-lg{
    filter:invert(1)
}
/*Buttons*/
.btn-outline-secondary{
    border-color:#333 !important;
    box-shadow: none!important;
    -webkit-box-shadow: none!important;
}
.btn-outline-secondary:hover{
    background-color:#151515;}
/*.btn-outline-secondary:focus{
    background-color:#151515 !important;}*/
.btn-outline-secondary:not(:disabled):not(.disabled):active,
.btn-outline-secondary:not(:disabled):not(.disabled).active,
.btn-outline-secondary.dropdown-toggle:focus{
    background-color:#333;
}
.pagination>.page-item>a,.pagination>.page-item.disabled>a{
    background-color:#151515;
    border-color:#333;}
.pagination>.page-item>a:hover{
    background-color:#555;}
.btn-secondary{
    background-color:#222;
    color:#AAA!important;
    border:none;}
.btn-secondary:hover{
    background-color:#444;
    color:#BBB;
    border-color:#666;}
.block-timeline .tab-content img{
    filter:invert(1);}
/*Dropdown menus*/
.dropdown-item:active,.dropdown-item.active{
    background-color:#444;}
/* Additions by Philip Damianik */
.bg-white {
    background-color: #151515!important;
}
.bg-light {
    background-color: #151515!important;
}
.border-top {
    border-top-width: 0px!important;
}
@keyframes bg-pulse-grey {
    0% {
        background-color: #333;
    }
    50% {
        background-color: #222;
    }
    100% {
        background-color: #333;
    }
}
.progress{
    background-color:#232323!important; /* color in this line edited by Philip Damianik */
    border: 0px solid #333!important; /* border removed by Philip Damianik */
    margin-bottom: 0.5rem; /* added by Philip Damianik */
    height: .35rem!important; /* added by Philip Damianik */}
.list-group-item {
    background-color: #151515!important;
}
.border-bottom {
    border-bottom: 1px solid #333!important;
}
#region-main, .formulation, .que .info, .moodle-dialogue-wrap, .quizreviewsummary .cell {
    background-color: #151515 !important;
    border-width: 0px!important;
    outline: none;
}
input {
    background-color: #151515 !important;
    border: 1px solid #AAA;
    outline: none;
}
.card, #page-enrol-users #filterform, .que .history, .userprofile .profile_tree section, .groupinfobox, .well {
    background-color: #151515 !important;
    border: 0px solid #0000!important;
}
.dashboard-card-deck {
    margin-top: 1rem;
}
.dashboard-card-deck .dashboard-card {
    border: 0px solid #0000!important;
    border-bottom: 1px solid #333!important;
}
.dashboard-card-deck .dashboard-card .card-body {
    background-color: #151515 !important;
    border-left: 1px solid #333!important;
    border-right: 1px solid #333!important;
}
.dashboard-card-deck .dashboard-card .card-footer {
    border-left: 1px solid #333!important;
    border-right: 1px solid #333!important;
}
.block_private_files .py-3 {
    padding: 0!important;
}
.block_private_files .footer {
    padding: 0!important;
    margin: 0!important;
}
div[data-region="event-list-loading-placeholder"] {
    background-color: #151515!important;
}
div[data-region="event-list-loading-placeholder"] > * {
    background-color: #151515!important;
}
#jump-to-activity {
    background-color: #151515!important;
}
.custom-select {
    border: 1px solid #666;
}
button {
    color: #666!important;
}
button *{
    color: #666!important;
}
button:focus {
    background-color: #151515!important;
}
.btn-outline-secondary:not(:disabled):not(.disabled):active, .btn-outline-secondary:not(:disabled):not(.disabled).active, .show>.btn-outline-secondary.dropdown-toggle {
    color: #151515!important;
    background-color: #666!important;
    border-color: #666!important;
}
.btn-outline-secondary:not(:disabled):not(.disabled):active *, .btn-outline-secondary:not(:disabled):not(.disabled).active *, .show>.btn-outline-secondary.dropdown-toggle * {
    color: #151515!important;
}
textarea.form-control {
    background-color: #222!important;
    border-width: 0px;
}
img[src="https://elearning.tgm.ac.at/theme/image.php/classic/core/1565168979/u/f1"] {
    filter:invert(1) hue-rotate(180deg);
}
img[src="https://elearning.tgm.ac.at/theme/image.php/classic/core/1565168979/i/loading_small"] {
    filter:invert(1) hue-rotate(180deg);
}
img[src="https://elearning.tgm.ac.at/theme/image.php/classic/block_progress/1565168979/right"] {
    filter:invert(1) hue-rotate(180deg);
}
.progressBarCell {
    border-color: #151515!important;
}
.firstProgressBarCell {
    border-right-width: 0px!important;
}
.message-app p span > .matchtext {
    background-color: #AAA!important;
    color: #333!important;
}
.message-app h6 > .matchtext {
    background-color: #AAA!important;
    color: #333!important;
}
input:focus, textarea:focus, select:focus{
    outline: none!important;
    box-shadow: none!important;
}
.popover-region-header-container {
    border-bottom: none;
}
.forumpost {
    border-width: 0px!important;
}
.sr-only, .sr-only-focusable {
    background-color: #222!important;
}
.disabled .icon {
    color: #333!important;
}
.page-item:not(.disabled) .icon {
    color: #666!important;
}

.path-mod-assign td.submissionnotgraded, .path-mod-assign div.submissionnotgraded {
    background-color: #151515;
    color: #AAA;
}

.correct {
    background-color: #151515!important;
    color: #AAA!important;
    border: none!important;
}

.incorrect {
    background-color: #151515!important;
    color: #AAA!important;
    border: none!important;
}

.path-mod-assign td.submissiongraded, .path-mod-assign div.submissiongraded, .correct .grade, .correct .state, .correct .content .outcome, .path-mod-assign td.earlysubmission, .path-mod-assign div.earlysubmission {
    background-color: #151515;
    color: #0cfc0c!important;
}

.path-mod-assign td.submissionstatussubmitted, .path-mod-assign div.submissionstatussubmitted, .path-mod-assign a:link.submissionstatussubmitted {
    background-color: #151515;
    color: #ffff5d;
}

.correct .trafficlight {
    background-image: none!important;
    background-color: #0cfc0c!important;
    color: #151515!important;
}

.correct .thispageholder {
    color: #AAA!important;
    border: 1px solid #AAA;
    border-radius: 0px;
}

.incorrect .trafficlight {
    background-image: none!important;
    background-color: #ff2c2c!important;
    color: #151515!important;
}

.incorrect .thispageholder {
    color: #AAA!important;
    border: 1px solid #AAA;
    border-radius: 0px;
}

.path-mod-assign td.submissionnotgraded, .path-mod-assign div.submissionnotgraded, .incorrect .grade, .incorrect .state, .incorrect .content .outcome, .incorrect input {
    background-color: #151515!important;
    color: #ff2c2c!important;
}

.path-mod-assign td.submissionnotgraded, .path-mod-assign div.submissionnotgraded {
    background-color: #151515;
    color: #26acd9;
}

.dndupload-arrow {
    filter: invert(1) hue-rotate(180deg);
}

.path-mod-assign td.latesubmission, .path-mod-assign a:link.latesubmission, .path-mod-assign div.latesubmission {
    background-color: #151515;
    color: #ff2c2c;
}
}
