css:
@-moz-document url-prefix("https://bb.tctc.edu/webapps"), domain("bb.tctc.edu") {
/**
 * TCTC Blackboard style fixes
 * @author Reed Davenport <reed@plusreed.com>
 * @license CC BY-NC-SA
 */
/** Custom CSS properties */
:root {
    --tctc-gray: #4D4D4D;
    --tctc-yellow: #DED3B0;
}

/** Changes the background of the top to match with the background of the TCTC logo. */
.topTabs {
    background-color: var(--tctc-gray);
}

/** Changes the appTab bottom border to match with the yellow from the TCTC logo. */
.appTabs td.active {
    border-bottom: 3px solid var(--tctc-yellow);
}

/** Fixes the pink color outline when clicking on a tab. */
.appTabs input:focus, .appTabs button:focus, .appTabs select:focus, .appTabs a:focus,
.global-nav-bar input:focus, .global-nav-bar button:focus, .global-nav-bar  select:focus, .global-nav-bar  a:focus,
.mybb-tools input:focus, .mybb-tools button:focus, .mybb-tools  select:focus, .mybb-tools  a:focus,
.mybb-div .side-nav input:focus, .mybb-div .side-nav button:focus, .mybb-div .side-nav select:focus, .mybb-div .side-nav a:focus,
.navPalette input:focus, .navPalette button:focus, .navPalette select:focus, .navPalette a:focus,
.gradeHeader input:focus, .gradeHeader button:focus, .gradeHeader select:focus, .gradeHeader a:focus, 
.gradingPanelHeader input:focus, .gradingPanelHeader button:focus, .gradingPanelHeader select:focus, .gradingPanelHeader a:focus,
.inlineGrader .resizeControls input:focus, .inlineGrader .resizeControls button:focus, .inlineGrader .resizeControls select:focus, .inlineGrader .resizeControls a:focus  {
    outline: 2px solid var(--tctc-yellow);
}

/** Experimental change: Chenge badge color to use TCTC colors. */
.global-nav-bar .badge, .side-nav .menu-icon span.badge {
    background-color: var(--tctc-yellow);
    color: var(--tctc-gray);
}

/** Experimental change: Change badge color in myBB tools to use TCTC colors.*/
.mybb-tools li span.badge {
    background-color: var(--tctc-yellow);
    color: var(--tctc-gray);
}

/** Experimental change: Change badge color to use TCTC colors. */
.badge {
    background-color: var(--tctc-yellow);
}

/** Experimental change: Change text color to use TCTC colors. */
.side-nav ul li a span.badge {
    color: var(--tctc-gray);
}
}
