css:
@-moz-document domain("www.crunchyroll.com") {
    /*[[theme]]*/
  :root{
    	--max-width: 1440px;
  }
    #header_beta {
        position: sticky!important;
        top: 0px!important;
        left: 0!important;
        z-index: 999999!important;
    }

    #footer,
        #footer_menu {
            width: 100%!important;
            max-width: none!important;
        }

        #showmedia_free_trial_signup {
            display: none!important;
            width: 0px!important;
            height: 0px;
        }
        #template_skin_leaderboard,
        #skin_splashlink {
            width: 100%!important;
            margin: 0px!important;
            padding: 0px!important;
        }
        #template_skin_leaderboard img {
            width: 100%!important;
            height: auto!important;
        }
        .store {
            background: inherit !important;
            background-color: inherit !important;
        }

        .season ul.portrait-grid {
            scroll-behavior: smooth;
        }

        #template_container,
        .cr-expo-banner {
            box-sizing: border-box!important;
            width: 100%!important;
            min-width: auto!important;
            max-width: none!important;
        }


        #template_container.template-container {
            box-sizing: border-box!important;
            width: 100%!important;
            min-width: auto!important;
            max-width: none!important;
        }


        #source_showview {
            width: 100%!important;
        }

        #source_showview h1 {
            width: 100%!important;
        }

        #main_content,
        #welcome_left,
        .genre-selectors,
        .season-selectors {
            width: 100%!important;
        }
        .portrait-bubble .tooltip-left,
        .game-banner-wrapper {
            display: none!important;
        }
        .portrait-bubble div {
            box-shadow: none;
        }
        #slidebox {
            width: auto;
            max-width: 90vw;
        }
        #sidebar,
        #welcome_right {
            width: 100%!important;
            box-sizing: border-box!important;
            margin: 0px!important;
        }


        #welcome_right #sidebar {
            padding: 10px!important;
            margin: 0px 0px!important;
            box-sizing: border-box!important;
        }

        #source_showview #container {
            display: flex!important;
            flex-direction: column!important;
        }
        #showview-content-header .ch-left {
            box-sizing: border-box!important;
            width: calc(100% - 340px)!important;
            margin-left: 355px!important;
            margin-top: 0px!important;
            position: absolute!important;
            float: none!important;
            z-index: 999;
        }
        #showview-content-header .ch-left h1 {
            font-size: 2.4em!important;
        }
        #source_showview #container #main_content {
            order: 30!important;
        }

        #source_showview #container #sidebar {
            order: 20!important;
            padding: 10px 0px!important;
        }

        #source_showview #container #sidebar #sidebar_elements {
            display: grid!important;
            grid-gap: 15px!important;
            grid-template: 'cover cover about about about' 'cover cover about about about' 'cover cover avail avail avail' 'cover cover rating details details'!important;
        }

        #source_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(1) {
            grid-area: cover!important;
            text-align: center!important;
        }
        #source_showview .poster {
            width: 340px!important;
            height: auto!important;
            min-height: auto!important;
            max-width: 340px!important;
        }
        #source_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(2) {
            grid-area: avail!important;
            margin-bottom: 0px!important;
        }

        #source_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(3) {
            grid-area: about!important;
            margin-bottom: 0px!important;
            padding-top: 30px!important;
        }

        #source_showview #container #sidebar #sidebar_elements li p.description .trunc-desc,
        #source_showview #container #sidebar #sidebar_elements li p.description .more-dots,
        #source_showview #container #sidebar #sidebar_elements li p.description span:last-of-type {
            display: none!important;
        }

        #source_showview #container #sidebar #sidebar_elements li p.description .more {
            display: inline!important;
        }

        #source_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(4) {
            grid-area: rating!important;
            margin-bottom: 0px!important;
        }

        #source_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(5) {
            grid-area: details!important;
            margin-bottom: 0px!important;
        }


        #source_manga_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(1) {
            grid-area: cover!important;
            text-align: center!important;
        }
        #source_manga_showview .poster {
            width: 280px!important;
            height: auto!important;
            min-height: auto!important;
            max-width: 300px!important;
        }
        #source_manga_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(2) {
            grid-area: about!important;
            margin-bottom: 0px!important;
            padding-top: 40px!important;
        }

        #source_manga_showview #container #sidebar #sidebar_elements li.large-margin-bottom:nth-of-type(3) {
            grid-area: more!important;
            margin-bottom: 0px!important;
        }

        #source_manga_showview #container #sidebar #sidebar_elements {
            display: grid!important;
            grid-template: 'cover cover about about about' 'cover cover about about about' 'cover cover more more more' 'cover cover more more more'!important;
            grid-gap: 15px!important;
        }


        #source_manga_showview #container {
            display: flex!important;
            flex-direction: column!important;
        }
        #source_manga_showview #container h1.ellipsis {
            box-sizing: border-box!important;
            width: calc(100% - 300px)!important;
            margin-left: 310px!important;
            margin-top: 0px!important;
            position: absolute!important;
            font-size: 2em!important;
        }
        #source_manga_showview #container #main_content {
            order: 30!important;
        }

        #source_manga_showview #container #sidebar {
            order: 20!important;
        }


        .show-actions {
            text-align: center!important;
        }

        #tabs {
            box-shadow: none!important;
            -moz-box-shadow: none!important;
            -webkit-box-shadow: none!important;
        }

        .main-tabs {
            padding: 0px!important;
        }

        .portrait-grid {
            display: flex;
            flex-wrap: wrap;
            justify-content: center!important;
        }

        .share-buttons {
            display: none!important;
        }

        table.comments {
            width: 100%!important;
        }
        .comments .guestbook-body {
            width: calc(100% - 70px)!important;
        }

        .welcome-featured-media,
        .banner-carousel .viewport {
            width: 100%!important;
            height: calc(100vw * 0.57)!important;
        }

        #showview_content_volumes {
            max-width: 640px!important;
            margin: auto!important;
        }

        @media only screen and (min-width: 1001px) {
            #source_browse #main_content,
            #welcome_left,
            #source_home #main_content {
                width: calc(100% - 400px)!important;
                box-sizing: border-box!important;
            }
            #source_browse .portrait-container {
                width: 100%!important;
            }

            #source_browse #sidebar,
            #welcome_right,
            #source_home #sidebar,
            #welcome_right #sidebar {
                width: 300px!important;
                box-sizing: border-box!important;
                padding: 0px!important;
            }
            .welcome-featured-media,
            .banner-carousel .viewport {
                width: 100%!important;
                height: calc((100vw - 400px) * 0.57)!important;
            }

            #source_home #main_content .series-info {
                width: calc(100% - 180px)!important;
            }

            #source_home #main_content .landscape-element {
                flex-grow: 1!important;
                flex-shrink: 1!important;
            }
            #source_home #main_content .series-info {
                flex-grow: 1!important;
                flex-shrink: 1!important;
                width: auto!important;
            }
        }
        #showmedia_video,
        #showmedia_video_box {
            width: 90vw!important;
            height: calc(90vw * 0.55)!important;
            margin: 5px auto 5px auto!important;
        }
        #template_container.template-container{
            width: 100%;
            max-width: var(--max-width);
        }
        @media only screen and (min-width: 1440px) {
            #template_scroller {
                width: 100%!important;
                max-width: var(--max-width)!important;
                margin-left: auto!important;
                margin-right: auto!important;
            }
            .welcome-featured-media,
            .banner-carousel .viewport {
                width: 100% !important;
                height: calc((var(--max-width) - 400px) * 0.57) !important;
            }
            #showmedia_video,
            #showmedia_video_box {
                width: calc(var(--max-width) * 0.9)!important;
                height: calc((var(--max-width) * 0.9) * 0.55)!important;
                margin: 5px auto 5px auto!important;
            }
        }
        #source_home #main_content .series-info {
            width: calc(100% - 180px)!important;
        }

        #main_content .landscape-element.episode .series-title {
            width: 100%!important;
        }



        .collection-carousel {
            width: 100%!important;
        }

        .collection-carousel-contents {
            width: calc(100% - 20px)!important;
        }

        #welcome_left {
            padding: 0px!important;
        }

        #welcome_left h1 {
            box-sizing: border-box!important;
            padding-left: 15px!important;
        }

        .welcome-featured-media .banner-carousel .overlay-content > * {
            display: block!important;
        }
        .welcome-featured-media .banner-carousel .overlay-action {
            display: none!important;
        }

        .welcome-simulcasts {
            height: auto!important;
            padding: 20px 0px 10px 0px!important;
        }
        ul.welcome-simulcasts {
            display: flex!important;
            flex-wrap: wrap!important;
            justify-content: center!important;
        }

        .widget-base-bubble {
            margin-left: 20px;
        }
        .welcome-simulcasts ~ .welcome-more-bottom,
        .widget-base-bubble-arrow-right,
        .widget-base-bubble-arrow {
            display: none;
        }

        .welcome-simulcasts li {
            height: 150px!important;
            box-sizing: border-box!important;
            margin: 5px!important;
            padding: 5px 0px 5px 5px!important;
            box-shadow: 0 0px 2px rgba(0, 0, 0, .45)!important;
            transition: box-shadow 0.3s, transform 0.3s!important;
            width: 270px!important;
        }
        .welcome-simulcasts li:hover {
            box-shadow: var(--shadow)!important;
            transform: var(--scale)!important;
        }

        .welcome-simulcasts .info {
            width: 90px!important;
        }
        .welcome-simulcasts .welcome-simulcasts-collection {
            height: auto!important;
            width: 100%;
        }
        .welcome-simulcasts .welcome-simulcasts-collection .name {
            position: static!important;
            width: 100%!important;
        }

        .welcome-simulcasts .welcome-simulcasts-episode,
        .showmedia-header h1.ellipsis {
            white-space: normal!important;
        }

        .welcome-crnews-item,
        .welcome-news-item {
            margin: 15px!important;
            padding: 5px!important;
            box-sizing: border-box!important;
            border-radius: 5px!important;
            box-shadow: 0 0px 2px rgba(0, 0, 0, .45)!important;
            transition: box-shadow 0.3s!important;
        }
        .welcome-crnews-item:hover,
        .welcome-news-item:hover {
            box-shadow: var(--shadow)!important;
        }

        .welcome-crnews-info,
        .welcome-news-info {
            width: calc(100% - 180px)!important;
        }

        .welcome-latest-videos {
            width: 100%!important;
            display: flex!important;
            flex-wrap: wrap!important;
            justify-content: center!important;
            padding-top: 10px;
        }

        .welcome-latest-videos li {
            padding: 8px!important;
            margin: 5px !important;
            box-shadow: 0 0px 2px rgba(0, 0, 0, .45)!important;
            transition: box-shadow 0.3s, transform 0.3s!important;
        }
        .welcome-latest-videos li:hover {
            box-shadow: var(--shadow)!important;
            transform: var(--scale)!important;
        }

        #sidebar .videos .landscape-element {
            width: 100%!important;
        }

        #sidebar .videos .landscape-element .series-info {
            width: calc(100% - 100px)!important;
        }

        .series-title.ellipsis {
            white-space: normal;
        }

        #sidebar .landscape-element .series-title,
        .short-desc {
            width: 100%!important;
            text-overflow: ellipsis!important;
            overflow: hidden!important;
        }
        #showview_content_videos li.season .portrait-grid {
            display: flex;
    		flex-wrap: nowrap !important;
    		overflow-x: scroll!important;
            justify-content: flex-start!important;
            box-sizing: border-box!important;
            margin: 0px!important;
        }

        .portrait-bubble {
            width: 184px!important;
            box-sizing: border-box!important;
            margin-left: -171px!important;
            margin-top: -130px!important;
            height: 250px!important;
        }
        .portrait-bubble div {
            width: 100%!important;
            box-sizing: border-box!important;
            height: 100%;
        }
        .portrait-bubble .portrait-desc p {
            height: auto;
            max-height: 80%;
            overflow: hidden;
            margin-top: 8px;
        }

        @media only screen and (max-width: 1000px) {
            #template_container,
            #template_container.template-containe {
                box-sizing: border-box!important;
                width: 100%!important;
                min-width: auto!important;
                max-width: none!important;
            }
            #source_browse #main_container {
                width: 100%!important;
            }

            #source_browse #sidebar {
                width: 100%!important;
                box-sizing: border-box;
                padding: 15px;
            }
            #SLB {
                display: none!important;
            }
            .comments .replylist {
                box-sizing: border-box!important;
            }
            .guestbook .guestbook-list {
                width: 100%!important;
                box-sizing: border-box!important;
                overflow: hidden!important;
            }
            .guestbook li {
                padding: 0px!important;
                box-sizing: border-box!important;
                width: 100%;
            }
            #main_content textarea {
                width: 80%!important;
            }
            #add_comment .bottom {
                width: 80%!important;
            }
            .news-left,
            .news-right {
                width: 100%!important;
                box-sizing: border-box;
            }

            .portrait-grid .add-queue-button {
                opacity: 1!important;
            }
            #sidebar ul.landscape-grid.shows {
                display: flex!important;
                flex-direction: column;
                flex-wrap: wrap!important;
                justify-content: space-between!important;
            }
            #sidebar ul.landscape-grid li.group-item {
                width: 100%!important;
            }
            #sidebar ul.landscape-grid.shows li {
                margin: 8px;
                transition: box-shadow 0.3s, transform 0.3s;
                flex-grow: 1;
            }
            #sidebar .landscape-element {
                width: 100%;
                box-sizing: border-box;
            }
            #sidebar .shows .landscape-element .series-info {
                width: calc(100% - 70px);
            }
            a .welcome-countdown-details {
                width: calc(100% - 111px);
            }
            .welcome-countdown-timer {
                height: auto;
            }
            #sidebar .landscape-element .series-title,
            .short-desc {
                white-space: normal!important;
            }
            .welcome-countdown-items li {
                margin-bottom: 15px!important;
            }
        }
        #sidebar ul.landscape-grid.shows li:hover {
            box-shadow: var(--shadow-light);
            transform: var(--scale);
        }
        #showforumtopic {
            width: 100%!important;
            margin: 10px auto 0 auto!important;
            position: relative;
            box-sizing: border-box!important;
        }
        .showforumtopic-header {
            width: 100%!important;
            box-sizing: border-box!important;
        }
        table.showforumtopic-messages {
            max-width: 100%!important;
            display: flex;
            flex-direction: column;
        }
        table.showforumtopic-messages tbody {
            width: 100%!important;
            display: flex!important;
            flex-direction: column!important;
        }
        table.showforumtopic-messages tbody tr {
            width: 100%!important;
            display: flex!important;
        }
        table.showforumtopic-messages tbody tr td.showforumtopic-message-user {
            width: 20%!important;
            height: auto!important;
            box-sizing: border-box;
        }
        .showforumtopic-message-user-footer {
            width: 20%!important;
        }
        .showforumtopic-message-user-thumblink,
        .showforumtopic-message-user-thumblink img {
            width: 100%!important;
        }
        .showforumtopic-message-user-name {
            width: 100%!important;
        }
        table.showforumtopic-messages tbody tr td.showforumtopic-message-contents {
            width: 80%;
            display: block;
            box-sizing: border-box;
        }
        .showforumtopic-message-footer {
            width: 80%;
            height: auto!important;
            box-sizing: border-box;
        }
        .showforumtopic-paginator div {
            display: flex;
            overflow-x: auto;
        }
        #forum_main,
        #forum_main_left,
        .forum_main-topics,
        .group-card,
        .showforumtopic-message-contents-header {
            width: 100%!important;
        }
        .showforumtopic-message-contents-text,
        .showforumtopic-message-contents-text .bb-quote {
            overflow: hidden;
            word-wrap: break-word;
            overflow-wrap: break-word;
            -webkit-hyphens: auto;
            -ms-hyphens: auto;
            hyphens: auto;
            width: 100%!important;
            box-sizing: border-box;
        }
        #forum_main_recentgroups {
            display: flex!important;
            flex-wrap: wrap!important;
        }
        #forum_main_recentgroups .group-card {
            box-sizing: border-box;
            display: flex;
            height: auto!important;
            position: static!important;
            width: 300px!important;
            margin: 5px;
            padding: 5px;
        }
        #forum_main_recentgroups .group-card .group-card-right {
            height: auto!important;
        }
        #forum_main_recentgroups .group-card .group-card-right .group-card-details {
            position: static!important;
        }
        #forum_main_right {
            float: none;
            padding: 0px;
            width: 100%;
        }
        .forum_main-topics ul li {
            margin: 8px 0px 8px 0px;
            transition: box-shadow 0.3s;
        }
        .forum_main-topics ul li:hover {
            box-shadow: var(--shadow-light);
        }
        .forum_main-topics ul li .forum_main-topic-desc {
            width: calc(100% - 70px);
        }
        .forum_main-topic-desc {
            padding-left: 10px;
        }
        .forum_main-topic-desc-text {
            width: calc(165px + (100% - 220px));
        }
        .forum_main-topic-desc-text-fullwidth {
            width: 100%!important;
            box-sizing: border-box!important;
        }
        a.forum_main-topic-desc-numcomments {
            border-radius: 10px;
        }
        .forum_main-topic-desc {
            min-height: 71px;
            height: auto!important;
        }
        .forum_main-topic-desc-text {
            height: auto!important;
        }


        .publisher-info,
        .publisher-info-container,
        .publisher-titles {
            width: 100%!important;
            box-sizing: border-box!important;
            margin: 0px!important;
            padding: 5px!important;
        }

        .publisher-titles {
            display: flex!important;
            flex-wrap: wrap!important;
            justify-content: center!important;
            padding: 20px!important;
        }

        .publisher-titles h2:first-of-type {
            width: 100%;
        }

        .publisher-title-container {
            margin-right: 40px!important;
            margin-left: 40px!important;
            margin-bottom: 10px!important;
            width: 162px!important;
            height: 150px!important;
        }


        @media only screen and (max-width: 650px) {
            .publisher-titles {
                display: flex!important;
                flex-wrap: wrap!important;
                justify-content: center!important;
                padding: 0px!important;
            }
            .publisher-title-container {
                margin-right: 5px!important;
                margin-left: 5px!important;
                margin-bottom: 10px!important;
                width: 162px!important;
                height: 150px!important;
            }

            .banner-carousel .slider-container .slide .img {
                width: 100%!important;
            }
            #showview-content-header h1.ellipsis,
            #source_manga_showview #container h1.ellipsis,
            #showview-content-header .ch-left {
                box-sizing: border-box!important;
                width: 100%!important;
                margin-left: 0px!important;
                margin-top: 0px!important;
                margin-bottom: 0px !important;
                padding-left: 10px;
                position: static!important;
                font-size: 1.5em!important;
                white-space: normal!important;
            }
            #showview_content_videos :not(li.season) .portrait-grid {
                display: flex!important;
                overflow-x: scroll!important;
                flex-wrap: nowrap!important;
                justify-content: flex-start!important;
            }
            #showview_content_videos div.other-series .portrait-grid {
                display: flex!important;
                overflow-x: scroll!important;
                flex-wrap: nowrap!important;
                justify-content: flex-start!important;
            }

            ul.welcome-simulcasts {
                display: flex!important;
                overflow-x: scroll!important;
                flex-wrap: nowrap!important;
                justify-content: flex-start!important;
                scroll-snap-type: mandatory;
                scroll-snap-points-x: repeat(280px);
                scroll-snap-type: x mandatory;
            }
            .welcome-simulcasts .welcome-simulcasts-collection {
                width: 276px;
            }
            .welcome-simulcasts {
                height: 180px!important;
                padding: 20px 10px 10px 10px!important;
            }

            ul.welcome-simulcasts li {
                scroll-snap-align: start;
                scroll-snap-stop: always;
            }

            ul.welcome-latest-videos {
                display: flex!important;
                overflow-x: scroll!important;
                flex-wrap: nowrap!important;
                justify-content: flex-start!important;
                scroll-snap-type: mandatory;
                scroll-snap-points-x: repeat(155px);
                scroll-snap-type: x mandatory;
                height: 200px;
            }
            ul.welcome-latest-videos li {
                scroll-snap-align: start;
                scroll-snap-stop: always;
            }

            #source_showview #container #sidebar #sidebar_elements {
                display: grid!important;
                grid-template: 'cover cover cover' 'about about about' 'avail avail avail' 'rating details details'!important;
                grid-gap: 10px!important;
            }

            #source_manga_showview #container #sidebar #sidebar_elements {
                display: grid!important;
                grid-template: 'cover cover cover' 'about about about' 'more more more'!important;
                grid-gap: 10px!important;
            }

            #source_showview #container #sidebar #sidebar_elements li.this-is-info {
                padding-top: 0px!important;
            }

            #footer,
            #global_base_bubble {
                display: none!important;
            }
            .welcome-featured-media .banner-carousel {
                min-height: auto!important;
            }
            .welcome-featured-media,
            .banner-carousel .viewport {
                width: 100%!important;
                height: calc(90vw * 0.57)!important;
            }
            .collection-carousel-arrow {
