css:
@-moz-document url-prefix("https://ria.ru/"), url-prefix("https://rsport.ria.ru"), url-prefix("http://ria.ru/"), url-prefix("https://rsport.ria.ru") {
.content {
    background-color: black!important;
}
.cell {
    background: #333;
}
.cell-extension__item-title {
    background: #555!important;
    color: #ccc
}
.cell-extension__item-title:hover {
    background: #777!important;
    color: #eee!important;
}
.footer {
    position: relative;
    background: #333!important;
    color: #ccc;
}
.the-in-scroll__content {
    background: #333!important;
}
.lenta__item-text {
    color: #ddd;
}
body.m-ria .color-font-hover-only {
    color: #999;
}
body.m-ria .color-font-hover-only:hover {
    color: #ddd;
}
.cell-author__author-title {
    color: #999;
}
.footer__rubric-item a {
    color: #999;
}
.footer__client-item a {
    color: #999;
}
.header__sticked {
    background: #333!important;
}
.header__menu-icon-l .svg-icon,
.header__menu-icon-s .svg-icon {
    width: 100%;
    height: 100%;
    fill: #ccc;
}
.cell-extension__list.the-in-carousel__ready .the-in-carousel.the-in-carousel__with-nav .the-in-carousel__nav {
    background: none;
}
.lenta__item-warn .lenta__item-size {
    background-color: #777;
}
.article {
    color: #ccc;
    background: #333;
}
.endless__item:first-child .layout-article {
    background: #000;
}
.header__brand-bg {
    background: #000;
}
.endless__item-bg {
    background: #000;
}
.article__userbar-item .emoji {
    background: #ccc;
}
.endless:not(.m-follow-fixed) .endless__item-sep {
    background: #111;
}
.article__tags-item {
    color: #777;
}
.article__tags-item:hover {
    color: #ddd;
}
.article__quote {
    background: #555;
}
.best-comments {
    color: #ddd;
    background: #555;
}
.best-comments__item-name {
    color: #ddd
}
.best-comments__item-message {
    color: #000;
    background-color: #ddd;
}
body.m-ria .color-btn,
.article.m-ria .color-btn {
    color: #eee;
    background-color: #222;
}
.header__brand .header__menu {
    background: #000;
}
.tag-input__tag {
    color: #eee;
}
.list-date {
    color: #ccc;
    background: #333;
    border: 0px solid #D9D9D9;
}
.pickmeup {
    background: #ccc;
}
.date-range__ranges {
    background: #ccc;
}
.date-range__ranges li {
    background: #ccc;
}
.list-item {
    background: #333;
}
.related-tags {
    background: #222;
    border: 0px solid #EAEAEA;
}
.list-more {
    color: #ccc;
    background-color: #777;
}
.list-more:hover {
    color: #777;
    background-color: #ccc;
}
.list-tag {
    border: 0px solid #CBCBCB;
    color: #777;
}
.header.m-media .header__brand-logo {
    background: #222;
}
.cell-photo__item {
    background: #aaa;
}
.cell-photo__info-date {
    color: #222;
}
.cell-question__read {
    color: #ddd;
    background: #777;
}
.cell-question__link:hover .cell-question__read {
    color: #777;
    background: #ddd;
    background-color: #ddd;
}
.section.m-theme-1d28ed .color-font-hover-only:hover {
    color: #777;
}
.rubric-content.m-theme-1d28ed .color-bg,
.rubric-content.m-theme-1d28ed .color-bg-hover,
.section.m-theme-1d28ed .color-bg,
.section.m-theme-1d28ed .color-bg-hover {
    background-color: #777;
    color: #ccc;
}
.rubric-content.m-theme-1d28ed .color-bg-hover:hover,
.section.m-theme-1d28ed .color-bg-hover:hover {
    background-color: #ccc;
    color: #777;
}
.mod-shape-2x1 .cell-list__item-title,
.mod-shape-3x1 .cell-list__item-title,
.mod-shape-4x1 .cell-list__item-title,
.mod-shape-6x1 .cell-list__item-title {
    color: #ddd;
}
.cell-list__item-link {
    color: #999!important;
}
.cell-list__item-link:hover {
    color: #ddd!important;
}
.cell-carousel__item-title {
    color: #999!important;
}
.cell-carousel__item-title:hover {
    color: #ddd!important;
}
.modal__preset {
    background: #222;
}
.modal__preset {
    color: #ccc;
}
.form__field {
    background-color: #777
}
.id-content {
    padding-bottom: 60px;
    background-color: #222;
}
.id-stat__item {
    color: #ccc;
}
.id-stat__item-value {
    color: #ddd;
}
.id-form__title,
.id-form__social-title {
    color: #ddd;
}
.id-form__row-name,
.id-form__social-switch {
    color: #ccc
}
.form__btn {
    color: #ccc;
    background-color: #777;
}
.form__btn:hover {
    color: #777;
    background-color: #ccc;
}
.form__field.m-inactive {
    background-color: #777;
}
.form__field.m-inactive:hover {
    background-color: #ddd;
}
.id-form {
    border: 0px solid #E4E3E3;
}
.modal__search-query {
    color: #ddd;
    background-color: #999!important;
}
body.m-ria .modal__search .modal__search-submit:hover {
    background-color: #777;
}
body.m-ria .modal__search .modal__search-submit {
    background-color: #222;
}
.r-list__item-title {
    color: #777;
}
.r-list__item-title:hover {
    color: #ccc;
}
.page {
    background: black;
}
.article__list-item {
    color: #eee;
}
}
