css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("friendfeed.com") {
*::-moz-selection{background:#333 !important;color:#fff !important;}
*{font-family: "tahoma", "Segoe UI","Arial","Serif" !important;color:#333 !important;font-size:11px !important;}

#body .box-body {background-color: #fff !important;}
#header {padding-bottom: 20px !important;}
.box .box-bar .box-bar-text, .box .box-bar .box-bar-text a {color:white !important;}
.box-bar-text .title {color:#fff !important;}

a{outline:none !important;}
a,a:link,a:visited{text-decoration:none !important;color: #1e529d !important;}
a:hover{color:#111 !important;border-bottom:1px dotted #333333 !important;}
a:active{text-decoration:none !important;color:#f4a460 !important;}

.subscribebar, .subscribebar a:link, .subscribebar a:visited, .control, .control a:link,.control a:visited {color: #fff !important;}
.friend{background-color:#ffff99 !important;color: #111 !important;}
.owner{background-color:#ccccff !important;color: #111 !important;}
.likes{background-color: #ffcccc !important;}
.lbody {font-style:italic !important;}
.updated{color: #ccc !important; padding: 1px 0 !important;}

.sharebox, .entry, .pager.top { border-bottom: 1px dotted #bbb !important; margin-bottom: 5px !important; padding-bottom: 15px !important; }
#footer a,#footer a:visited,.feed .entry .info a,.feed .entry .info a:visited,.feed .entry .likes a,.feed .entry .likes a:visited,.feed .entry .comment a,.feed .entry .comment a:visited,.feed .entry .expandcomment a,.feed .entry .expandcomment a:visited,.feed .entry.deleted a,.feed .entry.deleted a:visited,a.fl,a.fl:visited,.usersubgrid .lists a,.usersubgrid .lists a:visited{color: #333333 !important;}


.cluster{border-bottom: 1px dotted #708090 !important; padding-bottom:1em !important;}
.info,.likes,.comment,#footer,.pager,.bottom,.hidetoggle, #sidebar .box .box-body {}
.title {color:#000 !important;}

.searchbold {}
.main ul li a, .section ul li a {}
}
