css:
@-moz-document domain("csdn.net") {
.pulllog-box,
.tool-box,
#csdn-toolbar,
#reportContent,
.indexSuperise,
.meau-gotop-box,
#dmp_ad_58,
.mediav_ad,
.ad,
.csdn-side-toolbar,
.blog-expert-flexbox,
.recommend-fixed-box,
.recommend-right,
aside {
    display: none !important;
}
body,
.blog-content-box,
.recommend-box *,
.comment-box,
strong {
    background-color: #555 !important;
    color: #dedede !important;
    border-width: 0 !important;

    background: left top;
}
main div.blog-content-box .article-header-box,
.table-box * {
    background-color: #555 !important;
    color: #dedede !important;
}
.article-copyright,
.creativecommons > span:nth-child(2),
.article-source-link2222,
.markdown_views p {
    color: #dedede !important;
}
#content_views * {
    color: #dedede !important;
}
.btn-red,
.btn,
.tag-link,
.article_info_click,
.hljs-button {
    color: #fff !important;
    border: 1px solid #473f3f !important;
    background-color: #473f3f !important;
}
code {

    color: #fff !important;
    border: 0px !important;
    background-color: #555 !important;
}
.comment-box .comment-edit-box form .comment-content,
#myDrap {
    background-color: #555 !important;
}
.comment,
.name {
    background-color: #555 !important;
    color: #dedede !important;
}
.comment-box .comment-edit-box .user-img img {
    border: 0;
}
span.article-type.type-1 {
    color: #ca0c16;
    border: 1px solid #fff !important;
}
.prettyprint *,
blockquote {
    background-color: #473f3f !important;
}
.markdown_views .prettyprint,
.markdown_views pre.prettyprint {
    padding: 0px!important;
    padding-left: 49px!important;
    background-color: #dedede!important;
}
.markdown_views pre code,
.markdown_views .hljs {
    padding-left: 8px!important;
}
main {
    position: absolute;
    width: 1300px!important;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    margin: auto;
}
}
