css:
@-moz-document domain("trello.com") {
/*[fondo]*/

.board-header-btns > :last-child {
	display: block;
}

.card-detail-badge {
	padding: 2px 6px;
}

.card-label.mod-card-detail {
	height: unset;
	line-height: unset;
	padding: 0 6px;
}

.card-label.mod-card-front {
	max-width: 15px;
	min-width: 15px;
}

.board-header-facepile {
	display: none;
}

::-webkit-scrollbar {
	display: none;
}

/* List */
.list {
	min-height: 100px;
	background-color: rgba(255, 255, 255, .5);
	background: linear-gradient(to bottom,
	rgba(255, 255, 255, 0.8) 0%,
	rgba(255, 255, 255, 0.8) 10%,
	rgba(255, 255, 255, 0) 100%);
}

.list-card-title {
	font-size: 16px;
	line-height: 22px;
	margin: 8px 0 16px;
}

.list-card-details {
	padding: 12px;
}
.list-cards {
	margin: 0;
	padding: 0;
}

.list-card {
	border: 1px solid white;
	transition: border .4s;
}

.list-card.active-card {
	border: 1px solid #0079bf;
}

textarea.mod-list-name {
	padding: 0;
	font-weight: 400;
	text-transform: uppercase;
	font-size: 13px;
}

.list-card-members {
	margin-bottom: -10px;
}

.open-card-composer {
	color: white;
	background: rgba(255, 255, 255, .2);
}


/* Menu 3 puntos */
.list-header-extras {
	opacity: .1;
	transition: opacity 0.4s ease;
}
.list-header-extras:hover {
	opacity: 1;
}

/* Edit icon */
.icon-edit.list-card-operation {
	opacity: .2;
	transition: opacity 0.4s ease;
}

.icon-edit.list-card-operation:hover {
	opacity: 1;
}


/* Details */
.window {
	width: 70%;
	margin: 20px 0;
}
.window-header {
	margin: 20px 40px 8px 56px;
}
.window-main-col {
	width: calc(100% - 270px);
}
textarea.mod-card-back-title {
	font-size: 24px;
}

/* Labels */
.card-label.mod-card-front {
	padding: 1px 6px !important;
}
.card-label .label-text {
	text-transform: uppercase;
	font-size: 10px;
}

/* Badge */
.badge {
	text-transform: uppercase;
	font-size: 10px;
}

.badge-text {
	text-transform: uppercase;
	font-size: 10px;
}

/* Buttons */
.button-link {
	height: unset;
	padding: 4px 12px;
	margin: 4px 0;
	border-radius: 6px;
}

.phenom {
	border-bottom: 0;
}
.action-comment .current-comment {
	padding: 16px;
	font-size: 15px;
}
}
