.ac-title
{
    color: -moz-Dialogtext !important;
}

#PopupAutoComplete .autocomplete-treebody {
    background-color: -moz-Field !important;
    color: -moz-Dialogtext !important;
}

#urlbar[fission="fusion"] > #statusbar-icon > .progress-bar {
     background-color: Highlight !important;
     opacity:0.4 !important;
     margin: 1px !important;
}

/*My Additions Start HERE*/

/*Changes the font color of the menu on mouseover*/
menubar > menu[_moz-menuactive="true"] {
    color: black !important;
} 

/*Changes the background behind alot of stuff and kills borders. */
toolbar {
  -moz-appearance: none !important;
  border-left: 0px !important;
  border-right: 0px !important;
  border-bottom: 0px !important;
  border-top: 0px !important;
  background-color: #3A3A3A !important; 
}

/* change bookmark toolbar icon labels */
toolbarbutton.bookmark-item .toolbarbutton-text {
  color: #FFFFFF !important;
}

toolbarbutton.bookmark-item:hover .toolbarbutton-text {
  color: #000000 !important;
}

/*Changed Xs in tabs*/
toolbarbutton {
    background-color: #3A3A3A !important;
}

/*Changes the area behind tabs*/
.tabbrowser-tabs {
    background-color: #3A3A3A !important; border: 0 !important;
}

/*Active Tabs*/
tab{ -moz-appearance: none !important; } tab[selected="true"] {
    background: #3A3A3A url("data:image/png;base64,") repeat-x bottom;
    color: #FFFFFF !important; font-weight:bold !important;
    min-width: 200px !important; border: 0 !important;
}

/*Inactive Tabs*/
tab:not([selected="true"]) {
    background-color: #3A3A3A !important; 
    border-left: 2px solid !important; 
    -moz-border-left-colors: #A3A3A3 !important; 
    border: 0 !important;
    color: #FFFFFF !important;
}

/*Mouseover Inactive tabs*/
tab:not([selected="true"]):hover {
    background: #3A3A3A url("data:image/png;base64,") repeat-x bottom;
    color: #FFFFFF !important; 
    border: 0 !important;
}
