css:
@-moz-document domain("library.municode.com") {
body, .offcanvas-pane, .persistent-off-canvas, ul, ol, .list-unstyled, .quick-list-docs .quick-list-doc, .dropdown-menu > li > a, .dropdown-menu, .open > .dropdown-menu.animation-expand, .navbar-search input, .toc-zone-head, .zone-head, .toc-zone-body, .zone-body, .nav-stacked, .col-xl-1, .col-xl-2, .col-xl-3, .col-xl-4, .col-xl-5, .col-xl-6, .col-xl-7, .col-xl-8, .col-xl-9, .col-xl-10, .col-xl-11, .col-xl-12, section .style-default-bright, .offcanvas-pane .style-default-bright, .card .style-default-bright, section.style-default-bright, .offcanvas-pane.style-default-bright, .card.style-default-bright, p, header, .form-group .control-label, .well, .list-group-item, .card-body {
    color: #ddd !important;
        background-color: #333 !important;
}

.form-group > label, .form-control, .form-control:focus {
    color: #ddd;
}

a {
    color: #3ad;
}

ins, .ins {
    background-color: #250
}


.nav > li > a:hover, .nav > li > a:focus {
    color: #fd5;
    background-color: #025;
}


.hit {
    color: #000   
}

html {
    scrollbar-color: #444 #222;
}

img
{
  -webkit-filter: invert(80%) grayscale(100%); /* Safari 6.0 - 9.0 */
  filter: invert(80%) grayscale(100%);
}
}
