css:
@-moz-document domain("hvg.hu") {
html, html body, .body {
    background: #212121;
}

.site-header-nav {
    background: #292929;
    border-color: #212121;
}

body:not(.main-page) .container .maincontent,
body:not(.main-page) .container .sidebar .panelgap > *,
.height-equalizer,
.random-article-page-box,
.random-article-box-container,
body:not(.main-page) .container .heading-with_line,
.cimlap-grid.articlelist-element-wrap,
.footer,
.heading-with_line span,
.container,
.larger-header,
.latest-page,
.weather-panel > div,
.sitemain {
    background: #393939 !important;
    color: #FFFFFF !important;
}
body:not(.main-page) .container .maincontent a,
body:not(.main-page) .container .sidebar .panelgap > * a,
.random-article-box-container a,
.cimlap-grid.articlelist-element-wrap a,
.footer a {
    color: inherit;
}
.weather-panel > div span {
    color: #FFF;
}

.articlebox .more {
    color: #212121 !important;
}

a,
.site-header-nav .nav-categories .category:hover .category-button, .site-header-nav .nav-categories .category:focus .category-button, .site-header-nav .nav-categories .category:active .category-button, .site-header-nav .nav-categories .category.active .category-button,
.article-content .article-lead,
.article-content .article-title,
.article-content .article-info{
    color: #FFFFFF;
}

body:not(.main-page) .container {
    box-shadow: 0 0 1em #000;
}

.articleitem.bch-element [class^="heading"] a, .articlelist-element.bch-element [class^="heading"] a {
    background: none;
}
}
