/* CSS NÃO PROFISSIONAL | NOT PROFESSIONAL CSS */
@-moz-document domain("www.naoentreaki.com.br"), domain("naoentreaki.com.br") {
html {background: #101010;}
.submenu {background: #313131;} .promote-app, .submenu {border-bottom: 1px solid #2d2d2d;} .submenu .uk-navbar-nav>li>a {color: #ccc;}
.submenu .uk-navbar-nav>li:hover>a, .submenu .uk-navbar-nav>li>a:focus {color: #f51d4a;}
.uk-navbar {background: #3a3a3a;} .submenu {background: #272727!important;}
h1, h2, h3, h4, h5, h6 {margin: 0 0 15px;font-family: Roboto,sans-serif;font-weight: 400;color: #dcdcdc;text-transform: none;}
.uk-width-large-3-10 .uk-list h3 a {color: #dcdcdc;text-transform: uppercase;} .uk-width-large-3-10 .uk-list h3 a:hover {color: #cb002b;text-decoration: none;text-transform: uppercase;}
.uk-article-title a:hover {color: #cb002b;font-family: Roboto;text-decoration: none;text-transform: uppercase;}
.uk-link:hover, a:hover {color: #cb002b;text-decoration: none;}
.button-arriba:hover {background-color: #d72034;color: #fff;} .button-arriba {background-color: #883f3f;} 
.button-muerte {background-color: #737373;} .button-muerte:hover {background-color: #3c3c3c;}
.uk-article-title a {color: inherit;text-transform: uppercase;}
.uk-button:focus, .uk-button:hover {color: #ffffff;} .area-comment {padding: 10 !important; font-weight: 700;background: orange;}
.uk-button:focus, .uk-button:hover .area-comment {background-color: rgb(255, 118, 0);} 
.uk-button {color: #fff;}
a.uk-button.area-comment {background: orange; padding: 0;width: 150px;}
a.uk-button.button-arriba {width: 80px;text-align: left;}
.uk-width-large-3-10 .uk-list .uk-button {color: #ffffff;background: orange;}
.uk-article+.uk-article {border-top: 1px solid #1f1f1f;}
.uk-form-icon {position: static;}
.post-fixed-bar {background: #101010;}
.uk-panel-box-primary, .uk-panel-box-secondary {background-color: #1f1f1f;}
.uk-panel-box {border: 1px solid #3a3838;}
p.uk-margin-small.uk-text-break {color: #b9b9b9;}
.nzn-footer-group {display: none;} .nzn-footer-copy {background: #2b2b2b;}
.nzn-footer-copy, .nzn-footer-group, .nzn-footer-site {text-align: center;border-top: 1px solid #3e3e3e;}
span.uk-text-large {color: gainsboro;}
.uk-subnav>*>* {color: #dcdcdc;}
.uk-subnav-pill>*>:focus, .uk-subnav-pill>*>:hover {background: #cb002b;color: #f9f9f9;}
span.uk-article-title {color: gainsboro;}
.uk-block-muted {background: #1f1f1f;}
.uk-text-large {color: gainsboro;}
.uk-text-bold {color: gainsboro;}
.user-card h3 a, .users-widget .uk-list h3 a {color: gainsboro;}
.uk-panel-title { color: gainsboro;}
.uk-modal-dialog {background: #444;}
.uk-modal-header {border-bottom: 1px solid #565656;border-radius: 3px 3px 0 0;background: #444444;}
.post-thumbnail img, .post-thumbnail video {border-radius: 10px;}
.uk-width-large-3-10 .uk-list .uk-panel-teaser img {border-radius: 5px;}
.tag-select {border: 1px solid #565656;background: #5a5a5a;color: #222;}
strong.tag-title {color: gainsboro;}
span.tag-description {color: #f9f9f9;}
.main-menu .nzn-search-hint a:hover, .nzn-gallery .rsTab, .tag-select-links .tag a:hover, .uk-datepicker-nav a, .uk-datepicker-table a, .uk-width-large-3-10 .uk-panel-box-secondary a {text-decoration: none;background: #333333;}
}
