css:
@-moz-document domain(wpbeginner.com) {
body {
    background: #1a2427;
    color: #949494;
}

.content-block newonblog {
background: #181d1f;
}

.content-block pluginshome {
background: #181d1f;
}

.sidebar {
    background-color: #1a2427;
}

.sidebar .widget {
    background: #181d1f;
    opacity: 80%;
}


.sidebar {
    width: unset;
}

.sidebar-content .content, .content-sidebar .content {
    max-width: unset;
}

.content img, .content p img {
    filter: brightness(0.7);
}

.wpbthumb {
	opacity: 0;
}

}
