css:
@-moz-document url-prefix("http://jandan.net") {
* {
	border: 0px solid #000;
}
body {
	background: #000;
}
#header {
	margin-top: 0;
}
#content {
	background: #101010;
}
#content .title {
	background: #101010;
}
#content > h1, #cse-search-box, #float, #comments {
	background: #000;
}
h1, a, p, #sidebar > ul:nth-child(4) {
	color: #b8b8b8;
}
.author > strong {
	color: #f90;
}
/* 页码 */
 .current-comment-page {
	border: 1px solid #f90;
	color: #000;
	background: #f90;
}
.cp-pagenavi > a {
	background: #1f1f1f;
}
.cp-pagenavi > a, .cp-pagenavi > span {
	border-radius: 5px;
	line-height: 54px;
	font-size: 20px;
	white-space: nowrap;
	font-weight: 500;
}
.next-comment-page, .previous-comment-page {
	color: #f90;
	background-color: #000;
	border: 3px solid #f90;
	line-height: 48px;
}
/* 开关 */
.switch {
    padding: 10px 5px;
    border: 1px solid #f90;
}

#gif-click-load-on,
#nsfw-click-load-on,
#bad-click-load-on {
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
}

#gif-click-load-off,
#nsfw-click-load-off,
#bad-click-load-off {
    border-top-left-radius: 5px;
    border-bottom-left-radius: 5px;
}

.switch-current {
    background-color: #f90;
    color: #000;
}
/* 吐槽 */
 .tucao-content {
	color: #b8b8b8;
}
.tucao-form > .tucao-content {
	background-color: #101010;
}
.tucao-form button {
	background: #f90;
}
.tucao-author {
	color: #f90;
}
.tucao-hot-title {
	color: #fff;
	background: #101010;
}
.tucao-hot {
	background: #101010;
}
#nav_top {
	background-color: #f90;
}
}
