css:
@-moz-document domain("veryansintv.com")
{
::selection {
    background: #6f7579;
    color: #fff;
}

#tie-wrapper, 
header {
    background: rgb(34,41,49) !important;
}

nav, 
.ticker-content, 
.ticker-swipe, 
.breaking-title::before,
.entry-header-outer {
    background: #1f2024 !important;
}

#posts-list-widget-13,
#posts-list-widget-4 {
    padding: 20px;
    background: #1f2024 !important;
}

.container-wrapper {
    padding: 20px !important;
    background: #1f2024 !important;
}

.breaking-title {
    color:#bc2222 !important;
    text-transform: uppercase;
}

.ticker-content a {
    text-transform: uppercase;
}

.ticker-content a:hover {
    color:#bc2222 !important;
    text-transform: uppercase;
}

.menu a:hover,
.post-cat-wrap a:hover {
    color: #1f2024 !important;
}

h1, .entry-sub-title {
    font-size: 30px;
    color: #9ba1a8 !important;
}

figure img, 
.entry-content img {
    opacity: 0.5;
}
figure img:hover, 
.entry-content img:hover {
    opacity: 1;
}

a,
.entry-content,
.post-bottom-meta-title, 
.post-bottom-meta a, 
.post-meta, 
.post-meta a {
    color: #9ba1a8 !important;
}

h2 {
    font-size: 20px;
    color: #9ba1a8 !important;
}

h3, 
h4, 
.topbar-today-date, 
.breaking-news-nav {
    color: #fff !important;
}

a:hover {
    color: #3b8fcc !important;
}}
