css:
@-moz-document domain(www.hamster-joueur.com){
html {
    background-color: #222;
}
body {
    color: #e1e1e1;
    background-color: #222;
}
#wrapper {
    max-width: 90%
}
#content {
    max-width: 80%;
}
#header {
    background-color: #222;
}
#submenu > div > a {
    color: #edaf00!important;
}
#submenu {
    background-color: #222!important;
    border-bottom: 2px solid #edaf00!important;
}
input {
    background-color: #111;
    color: #edaf00;
}
.submitbox {
    background-color: #edaf00;
    color: #111;
}
.submitbox:hover {
    box-shadow: #edaf00 0 0 4px;
}
#connect {
    background-color: #edaf00;
    color: #111;
}
#connect:hover {
    box-shadow: #edaf00 0 0 4px;
}
#content #respond input {
    border: 1px solid #edaf00;
}
#register {
    background-color: #d59d01;
    color: #111;
}
#register:hover {
    box-shadow: #edaf00 0 0 4px;
}
#respond input[type=text]:focus,
textarea:focus {
    box-shadow: 0 0 5px #edaf00;
}
#content .entry {
    background-color: #151515;
    border: 1px solid #edaf00;
    text-align: justify;
}
#content .entry h1,
#content .entry h1 a {
    color: #edaf00;
    border-bottom: 0px solid #fff;
}
.voucher2 {
    margin-left: 10px;
}
.btnbpa {
    background-color: #edaf00;
    margin-left: 10px
}
.btnbpa:hover {
    box-shadow: #edaf00 0 0 4px;
}
.btnbpspn {
    color: #111;
}
.btnbpimg {
    filter: brightness(0%);
}
.sepbtmbp {
    border-bottom: 0px solid #edaf00;
}
a {
    color: #edaf00;
}
.imgcmtidx {
    filter: brightness(1.4);
}
.cmtidx {
    color: #edaf00;
}
.box {
    background-color: rgba(0, 0, 0, 0);
    color: #edaf00;
}
.box a {
    color: #edaf00;
}
#sidebar .col2 ul li {
    border-bottom: 1px solid #111;
}
#popusemmenu {
    color: #edaf00!important;
    border-bottom: 4px solid #edaf00!important;
    border-right: 0px dashed #CCC!important;
}
#popumoismenu {
    color: #edaf00!important;
    border-bottom: 4px solid #edaf00!important;
    border-right: 0px dashed #CCC!important;
}
#popuprecomenu {
    color: #edaf00!important;
    border-bottom: 4px solid #edaf00!important;
    border-right: 0px dashed #CCC!important;
}
#popusemmenu:hover {
    border-bottom: 4px solid #fff!important;
}
#popumoismenu:hover {
    border-bottom: 4px solid #fff!important;
}
#popuprecomenu:hover {
    border-bottom: 4px solid #fff!important;
}
#content #comments .commentlist li {
    background: #222;
    padding: 10px;
    border-bottom: 5px solid #edaf00;
}
#content #comments .commentlist li.alt {
    background-color: #111;
}
.children {
    background-color: #111;
}
#met {
    padding: 10px 10px;
    background-color: #000;
}
#content .pagination a {
    text-decoration: none;
    color: #edaf00;
}
.page-numbers {
    border: 1px solid #edaf00;
    margin: 2px;
    padding: 4px;
    border-radius: 4px;
}
.current {
    background-color: #edaf00;
    color: #000;
}
.reply {
    color: #edaf00!important;
}
.reply a {
    font: 400 12px Arial, Helvetica, sans-serif;
    background-color: #111;
    border-radius: 3px;
    border: 2px solid #edaf00;
}
.reply a:hover {
    border: #111 solid 2px;
}
.wpulike-default .wp_ulike_btn {
    color: #101010;
    background: #edaf00;
}
.wpulike-default .count-box {
    background-color: #101010;
    box-shadow: 0 0 0 1px #edaf00 inset;
    color: #edaf00;
}
.wpulike-default .count-box:before {
    background: #edaf00;
    border-bottom-color: #edaf00;
    border-left-color: #edaf00;
}
#bottom-nav {
    padding: 10px 10px;
    background-color: #000;
}
#content #comments h3 {
    color: #edaf00;
}
#content #respond h3 {
    font: bold 16px tahoma;
    background-color: #000;
}
#content #respond textarea {
    border: 1px solid #edaf00;
    background-color: #111;
    color: #fff;
}
#content #comments .commentlist #respond {
    background-color: #111;
    border: 1px solid #edaf00;
    margin-bottom: 5px;
}
#cancel-comment-reply-link {
    padding-left: 10px;
}
.titre,
.titre a {
    color: #edaf00!important;
}
.popusem div {
    border-bottom: 1px solid #333!important;
}
a:hover {
    text-shadow: #edaf00 0 0 2px;
}
#footermini {
    background-color: #000;
}
#footermini2 {
    background-color: #000;
}
#footersocial {
    background-color: #1e1e1e;
}
#footerfriendsblabla {
    background-color: #1e1e1e;
}
#footercopyright,
#footercopyright > div > p > a {
    background-color: #000000;
    color: #edaf00!important;
}blockquote {
    background-color: #000000;}}
