css:
@-moz-document domain("baka-tsuki.org") {
.catlinks{
    background-color: #383a3c;
}
div.thumbinner {
    background-color: #383a3c;
}
.mw-body {

    background-color: #484848;
    color: #ffffff;
    border: 1px solid #bfbfbf;
}
body {
    background-color: #535252;
    
}
h1, h2, h3, h4, h5, h6 {
    color: #fff;
}
a {
    text-decoration: none;
    color: #85b3ff;
    background: none;
}
.mw-body-content a.external:visited {
    color: #9d4f9d;
}
a:visited {
    color: #c085ff;
}
li.gallerybox div.thumb {
    border: 1px solid #a2a9b1;
    background-color: #717171;
}
#toc, .toc, .mw-warning, .toccolours {
    border: 1px solid #a2a9b1;
    background-color: #616161;
}
.tocnumber {
    color: #fff;
}
#mw-panel.collapsible-nav .portal.collapsed h3 a {
    color: #85b3ff;
}
#mw-panel.collapsible-nav .portal h3 a {
    color: #fbfbfb;
    text-decoration: none;
}
div#mw-panel div.portal div.body ul li a {
    color: #85b3ff;
}
#mw-page-base {
    height: 5em;
    background-image: linear-gradient(#757575 50%, #535252 100%);
    background-color: rgb(85, 84, 84);
    background-position: left bottom;
    background-repeat: repeat-x;
}
div.vectorTabs ul li {
    background-color: #bfbfbf;
    filter: invert(1) grayscale(1) contrast(0.45);
}
div.vectorTabs {
    border-left: 1px solid #bfbfbf;
    border-top: 1px solid #bfbfbf;
    border-right: 1px solid #bfbfbf;
    padding-left: 0px
}
#pt-anonuserpage {
    color: #ffffff;
}
table {

    background: #545454 !important;
    border: 1px solid gray;
    
}
div[style="position:relative; font-family: EasonPro, Helvetica, Arial, sans-serif; font-size: 22px; line-height: 30px; background:azure;margin:0.5em; text-align:center;"] {
   background: #484848 !important;
}
.mw-content-ltr:nth-child(1){
   background: #484848 !important;
}
.mw-body-content a.external {
    color: #85b3ff;
}
div#footer ul li {
    color: white;
}
div[style="border: solid 2px #A3B1BF; background: #F5FAFF; padding: 1em; margin: 1em 0;"] {
    background: #484848 !important;
}
span:first-child.b {
        color: #d4a2d4;
}
}
