css:
@-moz-document url-prefix("https://avgle.com") {
body {
    background-color: #000;
}
h1, h2, h3, h4, h5, h6 {
    color: #aaa;
}
.well {
    background-color: #000;
}
ul.navbar-right > li.hidden-sm, ul.navbar-right > li.hidden-xs {
    display: none;
}
ul.navbar-right > li {
    padding-right: 0 !important;
}
.col-md-8 {
    width: 100%;
}
.col-lg-4 {
    width: 25%;
}
.list-group {
  display: none;
}
#lp-confirm {
    display: none !important;
}
#wrapper {
    padding-bottom: 0 !important;
}
#wrapper > div {
    display: none;
}
#wrapper > div.container {
    display: block;
}
#ps32-container {
    display: none;
}
}
