css:
@-moz-document domain("fondationscp.wikidot.com") {
/* - - - - - - - - - - - - - - - - - - - - - - - */
/*                     MAIN                      */
/* - - - - - - - - - - - - - - - - - - - - - - - */

#page-title {
    color: #942828;
    background-color: #323232;
}

a{color: #942828;}

body {
    background-color: #323232;
    color: #a9a9a9;
}

div#container-wrap {
    background: url(http://scp-wiki.wdfiles.com/local--files/roget-s-workbench/header.png) top left repeat-x;
}

select{
    background-color:#a9a9a9;
}

input[type=button]{
	background-color:#a9a9a9;
    border:1px solid #a9a9a9;
    transition:0.3s;
}

input[type=button]:hover{
 	background-color:#323232;
    color:#942828;
    transition:0.3s;
    cursor:pointer;
}

/* Peut-être instable */
#page-content>div>.unmargined p{
	color:#942828;
}


/* ------------------------------------------------------------- */

#footer{
    display:flex !important;
    justify-content:space-between;
}

#footer .options{
	width:auto !important;
}

#license-area{
 	left:0 !important;
    width:100% !important;
    text-align:center;
    background-color:#444;
    color:#a9a9a9;
    opacity:0.3;
     transition:0.3s;
}

#license-area:hover{
	opacity:1;
    transition:0.3s;
}

#license-area::after{
    content:"~ Dark Theme proposé par Dr Kurai.";
    font-size: 90%;
    font-family: verdana,arial,helvetica,sans-serif;
    font-weight:bold;
    font-style:italic;
}

/* ------------------------------------------------------------- */

#search-top-box-input:hover,
#search-top-box-input:focus {
    background-color: #323232;
    color: #a9a9a9;
    outline:none;
    border:1px solid #b01;
}

#search-top-box input{
 	height:20px !important;
}

#search-top-box #search-top-box-input{
   	width:140px;
    text-align:center;
    background-color: #323232;
    color: #a9a9a9;
}

#search-top-box-form input[type=submit]{
    font-size:13px;
    background:none !important;
    background-color:#323232 !important;
    transition:0.3s;
}

#search-top-box-form input[type=submit]:hover{
    color:#b01;
    border:1px solid #b01 !important;
    transition:0.3s;
}

/* ------------------------------------------------------------- */


#top-bar a {
    color: #bbb;
    background: transparent;
}

#top-bar ul li.sfhover a,
#top-bar ul li:hover a {
    background: #323232;
    color: #a9a9a9;
}

#top-bar ul li.sfhover ul li a,
#top-bar ul li:hover ul li a {
    border-top: 1px solid #323232;
}

#top-bar ul li.sfhover a:hover,
#top-bar ul li:hover a:hover {
    background: #323232;
}

#top-bar ul li ul a,
#top-bar a:hover {
    color: #a9a9a9;
}


/* ------------------------------------------------------------- */

.rateBox{
    margin-bottom:10px;
}

.page-rate-widget-box {
    display: inline-block;
    margin-right: 5px;
    color: #a9a9a9;
}

.page-rate-widget-box .rate-points {
    background-color: #323232 !important;
    border: solid 1px #a9a9a9;
    border-right: 0;
    border-radius: 5px 0 0 5px;
    color: #a9a9a9;
}

.page-rate-widget-box .rateup,
.page-rate-widget-box .ratedown {
    background-color: #a9a9a9;
    border-top: solid 1px #a9a9a9;
    border-bottom: solid 1px #a9a9a9;
    font-weight: bold;
    color: #323232;
}

.page-rate-widget-box .rateup a,
.page-rate-widget-box .ratedown a {
    background: transparent;
    color: #323232;
    padding: 0 4px;
    margin: 0 1px;
}

.page-rate-widget-box .rateup a:hover,
.page-rate-widget-box .ratedown a:hover {
    background: #323232;
    color: #a9a9a9;
}

.page-rate-widget-box .cancel {
    background-color: #323232;
    border: solid 1px #a9a9a9;
    border-left: 0;
    border-radius: 0 5px 5px 0;
    color: #a9a9a9;
}

.page-rate-widget-box .cancel a {
    background: transparent;
    color: #a9a9a9;
}

.page-rate-widget-box .cancel a:hover {
    border-radius: 0 3px 3px 0;
    background: #323232;
    color: #a9a9a9;
}

.creditButton{
	background-color:#323232 !important;
    border:1px solid #a9a9a9 !important;
}

.creditButton:hover{
    border:1px solid #942828 !important;
}

.creditButton a{
	color:#a9a9a9;
}

.creditButton a:hover{
	color:#942828;
}

.heritage-rating-module{
    background-color:#323232 !important;
    border:none !important;
}

.heritage-rating-module img{
    margin-right:10px;
}

/* ------------------------------------------------------------- */
.modalbox.heritage{
 	background : none !important;
    background-color:#323232 !important;
    border: 2px solid #942828 !important;
    box-shadow:2px 2px 6px #942828, -2px -2px 6px #942828 !important;
}


.modalbox.heritage .modalbox-title{
	background: linear-gradient(to bottom, #a9a9a9, #323232) !important;
}


/* ------------------------------------------------------------- */


#side-bar .side-block{
    padding: 10px;
    border: 1px solid #a9a9a9;
    border-radius: 10px;
    margin-bottom: 15px;
    background-color: #323232 !important;
    color: #a9a9a9;
}

#side-bar .heading {
    border-bottom: solid 1px #a9a9a9;
    background-color: #323232;
    color: #a9a9a9;
}

#side-bar .collapsible-block-unfolded-link {
    border-bottom: solid 1px #a9a9a9;
    background-color: #323232;
    color: #a9a9a9;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link {
    margin-top: 10px;
    margin-bottom: 5px;
    color: #a9a9a9;
    background-color: #323232;
}

#side-bar .collapsible-block-unfolded-link .collapsible-block-link:hover {
    background-color: #323232;
    color: #a9a9a9;
}

#side-bar .new-page-box form input[type=text]{
	background-color:#a9a9a9;
    height:20px;
	margin:0 0 8px 0 !important;
}

#side-bar .new-page-box form input[type=text]:focus{
 	border:1px solid #824;
    outline: none;
}

#side-bar .new-page-box form input[type=submit]{
    background-color:#a9a9a9;
    border:1px solid #a9a9a9;
    height:25px !important;
    font-size:13px;
    font-variant: small-caps;
    transition:0.3s;
}

#side-bar .new-page-box form input[type=submit]:hover{
 	background-color:#323232;
    color:#942828;
    transition:0.3s;
    cursor:pointer;
}

#side-bar a,
#side-bar a:visited{
 	color:#b01 !important;
}
/* ------------------------------------------------------------- */


.scp-image-block .scp-image-caption {
    background-color: #323232;
    color: #a9a9a9;
}


/* ------------------------------------------------------------- */
.yui-navset .yui-nav a {
    background-color: #323232;
    background-image: none;
    color: #a9a9a9;
}

.yui-navset .yui-nav .selected a {
    background: none;
    background-image: none;
 	background-color:#700 !important;
}

.yui-navset .yui-nav a:focus {
    background: none;
    background-image: none;
    background-color: #323232;
    color: #101010;
}

.yui-navset .yui-nav a:hover {
    background: none;
    background-image: none;
    background-color: #a9a9a9;
    color: #101010;
}

.yui-navset .yui-content {
    background-color: #a9a9a9;
    color: #323232;
}


/* ------------------------------------------------------------- */


blockquote {
    background-color: #a9a9a9;
    color: #323232;
    border: 1px dashed #323232;
}


/* ------------------------------------------------------------- */


.content-panel {
    background-color: #323232 !important;
    color: #a9a9a9 !important;
    box-shadow: none;
}



.content-toc {
    background-color: #a9a9a9 !important;
    color: #323232 !important;
    border: none !important;
}


/* ------------------------------------------------------------- */


.footnote, .hovertip {background: #323232 !important;}

.hovertip {border: 1px solid #a9a9a9 !important;}


/* ------------------------------------------------------------- */


.post .head,
.description-block {
    background: none !important;
    background-color: #a9a9a9 !important;
    color: #323232;
}

.description-block {
    border-radius: 0px;
    border: 1px solid #a9a9a9 !important;
    box-shadow: none !important;
}

.post .long{
    background-color:#4C4C4C;
    border-radius:5px;
}

.post .long .content{padding:5px;}

.post .long .content a{color:#b01;}

/* ------------------------------------------------------------- */


.code {
  background-color: #a9a9a9;
  color: #323232;
  border: 1px dashed #323232;
}

#new-post-button {
  background-color: #a9a9a9;
  border: 1px solid #323232;
}

.pager span a {border: 1px solid #a9a9a9;}

.form-control {background-color: #a9a9a9 !important;}

#new-post-form .buttons input {
  background-color: #a9a9a9;
  border: 1px solid #323232;
}


/* ------------------------------------------------------------- */

.modalbox{
    background-color: #5c5b5b;
}

.modalbox-title{
    background-color:#6B0C0C !important;
}

/* ------------------------------------------------------------- */

.feature-block .content-panel .panel-body{
    background:none !important;
    background-image: linear-gradient(to bottom, #323232, #795D5D , #323232) !important;
}

.feature-block .content-panel .panel-heading{
	border-bottom:1px dashed #a9a9a9  !important;
    padding-bottom:5px;
    font-variant:small-caps;
    font-size:13px;
}

.feature-block .content-panel .panel-footer{
	border-top:1px dashed #a9a9a9  !important;
    padding-top:5px;
}

.feature-block .content-panel .panel-footer p::before,
.news-block .panel-footer p::before{
 	content:"► ";
}

.feature-block .content-panel .panel-footer p a,
.news-block .panel-footer p a{
 	transition:0.3s;   
}

.feature-block .content-panel .panel-footer p a:hover,
.news-block .panel-footer p a:hover{
	text-decoration:none;
    color:#942828;
    transition:0.3s;
}

/* ------------------------------------------------------------- */
.news-block{
    margin-top:25px;
}

.news-block .panel-heading,
.international-block .panel-heading{
    padding-bottom:5px;
    font-variant:small-caps;
    font-size:13px;
}

.news-block .panel-body .alternate:nth-child(odd),
.international-block .panel-body .international-entry:nth-child(odd)
{
    /*background-color:#616161;*/
    background-image:linear-gradient(to top, #616161, #909090) !important;
    color:black;
}

.news-block .panel-body .alternate:nth-child(even),
.international-block .panel-body .international-entry:nth-child(even){
    /*background-color:#909090;*/
    background-image:linear-gradient(to top, #909090, #616161) !important;
    color:black;
    border-top:none !important;
    border-bottom:none !important;
}

/* ------------------------------------------------------------- */
.wiki-content-table th{
    background-color: #a9a9a9 !important;
    color:#901;
}

/* ------------------------------------------------------------- */

.scpnet-interwiki-wrapper{
    border: 1px solid #a9a9a9;
    border-radius: 10px;
    margin-bottom: 15px;
    background-color: #323232 !important;
    color: #a9a9a9;
    height:20px;
    cursor:pointer;
}

.scpnet-interwiki-wrapper:hover{height:auto;}
.scpnet-interwiki-wrapper .list-pages-box{visibility: hidden;}

.scpnet-interwiki-wrapper .list-pages-box::before{
    content:"▼ Autres langues ▼";
    color:#b01 ;
    visibility: visible;
    font-weight:bold;
    font-family: verdana,arial,helvetica,sans-serif;
    text-align:center;
   	display: inline-block;
    width:100%;
}

.scpnet-interwiki-wrapper .list-pages-box:hover{visibility: visible;}


/* ------------------------------------------------------------- */


#toc{background-color:#a9a9a9;}


/* ------------------------------------------------------------- */


#login-status>.printuser::before{
 	content:"Bienvenue, ";
    color:#999;
    font-weight:bold;
    font-size:12px;
    font-family: verdana,arial,helvetica,sans-serif;
    font-variant:small-caps;
}

#login-status>.printuser{
 	color:#942828;
    font-weight:bold;
    font-size:12px;
    font-variant:small-caps;
}

#login-status>#my-account{
    color:#999;
    font-weight:bold;
    font-size:12px;
    font-variant:small-caps;
}

#login-status>#account-options{
    background-color:#323232;
    border:1px solid #323232 !important;
}

#login-status>#account-options a{
    color:#b01 !important;
    transition:0.3s;
}

#login-status>#account-options a:hover{
    color:#d61 !important;
    transition:0.3s;
    text-decoration:none;
}


/* ------------------------------------------------------------- */


.forum-group div.head{
    margin-top:20px !important;
}

.forum-category-box table tr.head td,
.forum-group table tbody .head td{
    background-color:#666 !important;
    color:white;
    font-weight:bold;
}

/* ------------------------------------------------------------- */

.new-post a{
	background-color:#323232 !important;
    border:1px solid #a9a9a9 !important;
    color:#a9a9a9;
    transition:0.3s;
}

.new-post a:hover{
	background-color:#323232 !important;
    border:1px solid #942828 !important;
    color:#942828;
    transition:0.3s;
    text-decoration:none;
}
}
