css:
@-moz-document url-prefix("https://medium.com") {
  @media (prefers-color-scheme: dark) {

      /* Disable */
      div:not(.bp) {
          background: transparent !important;
          background-color: transparent;
      }

      /* Text */
      div.u-textColorNormal,
      h1.u-textColorDark,
      p, span, div, figcaption, li, a, h1, h2, h3, h4 {
          color: #f8f8f2 !important;
      }

      /* Root */
      body {
          background-color: #282a36 !important;
          color: #f8f8f2 !important;
      }

      /* Popup */
      .bp { 
          background-color: #282a36 !important;
          box-shadow: rgba(0, 0, 0, 0.3) 0px 0px 20px !important;
      }

      /* Logo */
      svg, .aw, .ax {
          fill: #f8f8f2 !important;
      }

      /* Buttons */
      button { border-color: #f8f8f2 !important; }
      button:selected { color: #f8f8f2 !important; }

      /* Navigation */
      nav {
          background-color: #6a737d33 !important;
          backdrop-filter: blur(10px) saturate(125%);
          padding-bottom: 5px;
      }

  }
}
