css:
@namespace url(http://www.w3.org/1999/xhtml); 
	
@-moz-document domain(xiaonei.com), domain(renren.com) {
	body, select, label, textarea, input {
		font-family: sans !important;
	}
	#tobestar, .blank-holder, .ad-bar, .side-item.selected, .side-item.sales-poll, .side-item.point, .home-body #info, .xn2rr, #vipExpireTooltip, #paintself, #paintother, div[id^="logo"] h1, .profile-point, #userPoint, .avarar-tabs, .profile-actions, .profile-summary, .webpager-button, .info-edit .menu-title, .nav-other .menu:nth-child(1), .nav-other .menu:nth-child(2), .nav-other .menu:nth-child(3){
		display: none !important
	}
	.profile-avatar {
		margin-bottom: 10px !important;
	}
	.navigation, .navigation .nav-main, .navigation .nav-body, .navigation .nav-other {
		background: none !important;
		margin-left: 0 !important;
		border: none !important;
	}
	.menu-bar {
		background: url(data:image/gif;base64,R0lGODlhAgAiAMQAAPT09ff3+PPz9Pb39/Py8//+/vj4+fj5+fHw8e7u7+/v8PHx8e/u8O/v8f38/fb19v7+/fP09Pn5+vj4+PDw8fz8/Pb29u3u7vT19fr6+u7t7v7+/vv7+/Ly8v39/f///yH5BAAAAAAALAAAAAACACIAAAUz4Cd+xbedkOc5a+VyMJdlknQY0xQEQ2BZjwcGAwAIIgJCZ9lZLBALirShUDAYicRFsw0BADs%3D) repeat-x left top !important;
		margin-bottom: 10px !important;
		border-bottom: none !important;
	}
	.navigation-wrapper {
		border-bottom: 1px solid #ccc !important;
		width: auto !important;
		margin: 0 !important;
	}
	.navigation {
		margin: 0 auto !important;
	}
	#navBar div[id^="logo"] {
		display: block !important;
		margin-top: 8px !important;
		border: none !important;
		background: url(data:image/png;base64,) no-repeat center center !important;
		height: 20px !important;
		width: 101px !important;
	}
	.navigation .menu-title a {
		font-size: 13px;
		color: #005EAC !important;
	}
	.navigation .menu-title a .count{
		color: #005EAC !important;
		padding-left: 5px !important;
	}
	.navigation .menu-title a:hover {
		background-color: transparent !important;
		text-decoration: underline !important;
	}
	.navigation .menu {
		height: 34px !important;
		line-height: 34px !important;
	}
	#navSearch #search-input #navSearchInput {
		height: 17px !important;
		padding: 2px 4px !important;
	}
	.search-mag-glass {
		padding: 0 !important;
		height: 21px !important;
	}
	.lively-user {
		color: #005EAC !important;
	}
}
