css:
@-moz-document regexp('https://www.google.(com|(co.*))/search\??(.*)') {
    :root {
      --view-port: calc(50% - 622px);
    }

    @media (min-width:1251px){
        /* Search Bar */
        form.tsf.nj {
            margin-left: var(--view-port);
        }
        
        /* Options Bar */
        #hdtbSum {
            margin-left: var(--view-port);
        }
        
        /* Result Status Bar */
        #resultStats {
            margin-left: var(--view-port);
        }
        
        /* Searching Pages Area */
        .mw {
            margin-left: var(--view-port);      
        }
    }
}
