css:
@-moz-document domain("mp3cyborg.com"), domain("videocyborg.com"), domain("fbcyborg.com"), domain("instacyborg.com"), domain("tweetcyborg.com") {
/* scrollbar */
::-webkit-scrollbar {
    width: 16px;
    border-radius: 1px;
    border: 1px solid #101010;
    background: #101010;
}

::-webkit-scrollbar-thumb {;
    border-radius: 10px;
    border: 4px solid #101010;
    background-color: #545454;
}

::-webkit-scrollbar-thumb:active {
    background: #5f5b5bb0;
    border: 4px solid #101010;
}


/* Barra superior */
.nav {
    
    background-color: #242526;
    box-shadow: 0 1px 2px #3a3b3c;
    border-bottom: 2px #0000001a;
}
.nav a {
    color: #999;
}
/*----------------------*/
/* body pagr principal */

html {
   
    background-color: #18191a;
    }

.title {
    color: #999;
}
.apps {
    color: #dedede;
}
.oc-title {
    color: #878787;
}
.categoryapps {
    color: #999;
    border-bottom: 3px solid #999;
}
.footer {
    color: #999;
}
.tc {
    color: #999;
}
/*------------------*/
/* barra select o link input */

.url-input {
    border-radius: 58px;
}
textarea,
input,
select {
    color: #878787;
    background: #252627;
    border: 2px solid #0000001a;
}
textarea:focus,
input:focus,
select:focus {
    color: #fff;
    border-color: #0000001a;
}
/*----------------------------*/

/* barra de progreso*/

.cssProgress .cssProgress-bar {
    display: block;
    float: left;
    width: 0%;
    height: 100%;
    background: #1d1c1c;
    box-shadow: inset 0 -1px 2px rgba(0, 0, 0, .1);
    -webkit-transition: width .8s ease-in-out;
    transition: width .8s ease-in-out
}
.cssProgress .cssProgress-label {
    position: absolute;
    overflow: hidden;
    left: 0;
    right: 0;
    color: #fff;
    font-size: 1.1em;
    text-align: center;
    font-weight: 700
}
.cssProgress .cssProgress-info {
    background-color: #9575cd!important
}
.cssProgress .cssProgress-danger {
    background-color: #ef5350!important
}
.cssProgress .cssProgress-success {
    background-color: #66bb6a!important
}
.cssProgress .cssProgress-warning {
    background-color: #ffb74d!important
}
.cssProgress .cssProgress-right {
    float: right!important
}
.cssProgress .cssProgress-label-left {
    margin-left: 10px;
    text-align: left!important
}
.cssProgress .cssProgress-label-right {
    margin-right: 10px;
    text-align: right!important
}
.cssProgress .cssProgress-stripes,
.cssProgress .cssProgress-active {
    background-image: -webkit-linear-gradient( 135deg, rgba(255, 255, 255, 0.125) 25%, transparent 25%, transparent 50%, rgba(255, 255, 255, 0.125) 50%, rgba(255, 255, 255, 0.125) 75%, transparent 75%, transparent);
    background-image: linear-gradient( -45deg, rgba(255, 255, 255, 0.125) 25%, transparent 25%, transparent 50%, rgba(255, 255, 255, 0.125) 50%, rgba(255, 255, 255, 0.125) 75%, transparent 75%, transparent);
    background-size: 35px 35px
}
.cssProgress .cssProgress-active {
    -webkit-animation: cssProgressActive 2s linear infinite;
    -ms-animation: cssProgressActive 2s linear infinite;
    animation: cssProgressActive 2s linear infinite
}
.progress1 {
    background-color: #1d1c1c;
    box-shadow: inset 0 1px 3px rgba(0, 0, 0, .2)
}
.progress1 .cssProgress-bar {
    height: 28px
}
.progress1 .cssProgress-label {
    line-height: 28px
}
/*----------------*/

/* cuadro support */

.background-contribute {
    background-color: #242526;
    border-radius: 7px;
    box-shadow: 0 1px 2px #0000001a;
    box-sizing: border-box;
}
.text-question-contribute {
    color: #999;
}
.text-main-contribute {
    color: #878787;
}
.btn.contrib {
    background: #606060;
    color: #242526;
}
.btn.contrib:hover {
    background: #444444;
    color: #878787;
}
/*------------------*/
}
