css:
@-moz-document url-prefix("http://pukawka.pl/") {
body {
    background: #000;
}
.panel_logowania {
    background: #000;
}
.footer {
    background: #000;
}
.cookie-warn {
    height: 0px;
    width: 0px;
}
}

@-moz-document url-prefix("https://admin.pukawka.pl/") {
body {
    background: #000;
    color: #000;
}
.lang {
    background: #000;
}
.main {
    background: url("http://ericsson2506.ovh/stylish/pukawka/bg.png") no-repeat center top;
}
.content {
    margin-top: 10px;
}
.separator .msg_box {
    background: #000;
    height: 0px;
}
.separator p {
    padding-top: 20px;
    padding-bottom: 10px;
}
h3 {
    background: #333;
    height: 25px;
}
.adf_news h3 {
    background: #222;
    height: 30px;
}
.separator {
    background: #000;
}
.adf_txt {
    background: #111;
}
.footer {
    background: #000;
}
.separator .help_box {
    background: #000;
}
.separator #wt, .separator .tel, .separator .tel_info {
    color: #fff;
    padding-left: 80px !important;
    text-shadow: 0px 0px 0px #000 !important;
}
.separator .fk {
    color: #fff;
    padding-right: 0px;
    height: 23px;
    width: 170px;
    background: #333;
    left: 60px;
}
.topbar {
    color: #1DC9FF;
    font-size: 18px;
}
.controls-table {
    padding-top: 30px;
}
.server_info, .server_tracker, .srv_menu {
    margin-top: 30px;
}
}
