css:
@-moz-document domain("www.deepl.com") {
html,
body,
.dl_translator_page_container,
.lmt__dict__inner {
    background-color: #222 !important;
    color: azure !important;
}


.dl_header,
textarea {
    background-color: #333 !important;
    color: azure !important;
}

#lmt_quotes_article,
.lmt_quotes__container,
footer {
    display: none;
}

.lmt__source_textarea__length_marker {
    color: azure !important;
}

strong,
#text {
    color: darkgray !important;
}

.dictLink,
.formLink {
    color: #105030 !important;
}

.translate_from,
.translate_to,
.lmt__language_select--source > button:nth-child(1) > div:nth-child(2),
.lmt__language_select--target > button:nth-child(1) > div:nth-child(2) {
    color: #ddd;
}

.lmt__language_select--source > div:nth-child(2),
.lmt__language_select--source > div > button,
.lmt__language_select--target > div:nth-child(2),
.lmt__language_select--target > div > button {
    background-color: #444;
    color: azure !important;
}

.dl_header_menu_v2__logo > span:nth-child(2),
#dl_menu_translator,
#dl_menu_linguee,
.dl_header_menu_v2__login_button > button:nth-child(1),
.dl_header_menu_v2__buttons__menu > button:nth-child(1),
.dl_header_menu_v2__buttons__menu,
.dl_header_menu_v2__buttons__menu > button::before,
.dl_header_menu_v2__buttons__menu > button::after,
.lmt__language_select--source > button:nth-child(1) > span:nth-child(1) {
    color: azure !important;
}

.lmt__target_toolbar .lmt__target_toolbar__generic > button,
.lmt__target_toolbar .lmt__target_toolbar__copy > button,
.lmt__target_toolbar .lmt__target_toolbar__save > button,
.lmt__target_toolbar .lmt__target_toolbar__share > button {
    stroke: azure !important;
}
}
