css:
@-moz-document regexp("(http|https):\\/\\/www\\.furvilla\\.com\\/(?!profile\\/[0-9]+)(?!villager\\/[0-9]+).*")
{
/* CSS by Leaf on Furvilla
 * The CSS is a bit messy, but does the job.
 * Enjoy!
 * Note for self: Alternative bright blue: #83edfc */



  /* Messages */
  .table tr:first-child
  {
    background-color: #242424;
    border: 1px solid #ccc;
    border-width: 1px 1px 0px 1px;
    color: #ffa34f;
  }

  .table tr:first-child > th
  {
    border: none;
    font-weight: 300 !important;
  }
  
  /* Forum / Main */
  body
  {
    background-image: url("http://i.imgur.com/UF6NVb0.png") !important;
    background-color: #242424;
  }

  b
  {
    color: #ffa34f;
    font-weight: 300;
  }

  blockquote
  {
    color: #ccc;
  }

  .alert.info
  {
    background-color: #242424;
    border-radius: 25px;
    border: 1px solid #ccc;
  }

  .alert.info a
  {
    font-weight: 300;
    font-size: 14px;
    letter-spacing: 0.5px;
  }

  .alert.info:hover
  {
    background-color: #111;
  }

  .profanity-filter.thread-post
  {
    background-color: #242424;
  }

  .widget-header h3
  {
    background-image: none;
    border: 1px solid #ccc;
    border-width: 1px 1px 0px 1px;
    margin: 0;
    background-color: #242424;
    color: #ffa34f !important;
    font-weight: 300;
  }

  .widget-content
  {
    background-color: #242424;
    border: 1px solid #ccc;
  }

  .widget-header
  {
    border: none;
  }

  .content
  {
    background-color: #242424;
  }

  .content:after
  {
    background-image: none;
  }

  nav
  {
    background-image: none !important;
    background-color: transparent !important;
  }

  .user-panel
  {
    background-color: rgba(255,255,255,0.1) !important;
    color: #ccc !important;
  }

  .user-panel a, header nav a
  {
    color: #ccc !important;
  }

  .notifications td
  {
    background-color: transparent !important;
    border: none !important;
  }

  .notifications tr:nth-child(even)
  {
    background-color: transparent !important;
    color: #aaa;
  }

  .notifications tr:hover td
  {
    background-color: #111 !important;
  }

  a
  {
    color: #73bdec;
    font-weight: 300;
  }

  .user-info h4 a
  {
    color: #73bdec !important;
  }

  .user-info strong, .user-info, .user-info a, .user-info h4
  {
    color: #ffa34f;
    font-weight: 300 !important;
  }

  small .btn-link
  {
    color: #ffa34f !important;
    font-weight: 300;
  }

  .notifications tr:nth-child(odd)
  {
    background-color: transparent !important;
    color: #aaa;
    border: 1px solid black;
    border-width: 1px 0px 1px 0px;
  }

  .content h1
  {
    color: #ccc;
  }

  .breadcrumbs
  {
    color: #73bdec;
  }

  .textarea
  {
    background-color: #242424;
    color: #ccc;
  }

  .thread-user-info
  {
    background-color: transparent !important;
    border: 1px solid #ccc;
    border-width: 1px 0px 1px 1px;
    color: #242424 !important;
  }

  .thread-user-info a
  {
    text-shadow: 0 0 5px #242424 !important;
  }

  b .profanity-filter.thread-post
  {
    background-color: #242424;
  }

  .recent-posts li
  {
    background-color: transparent !important;
  }

  .recent-posts li:hover
  {
    background-color: #111 !important;
  }

  ::selection
  {
    color: #ccc !important;
    background: #222 !important;
  }

  .pagination li a, .pagination li.active span, .pagination li.active span:hover
  {
    background-color: #222 !important;
    border-color: #ccc;
    color: #73bdec;
  }

  .pagination li a:hover, .pagination li.active span:hover
  {
    background-color: #111 !important;
  }

  .pagination li.active span, .pagination li.active span:hover
  {
    color: #ffa34f;
    background-color: #222 !important;
  }

  .btn
  {
    background-image: none !important;
    background-color: #222;
    border: 1px solid #ccc;
    font-weight: 300;
    color: #ffa34f !important;
  }

  .btn:hover
  {
    background-image: none !important;
    background-color: #111;
    color: #73bdec !important;
    border: 1px solid #ccc;
  }

  a:hover
  {
    color: #07909a;
  }
  /* Inventory */
  .inventory-item-info
  {
    background-color: #222;
  }

  .inventory-item .counter
  {
    color: #ffa34f;
  }

  .inventory-item-info:hover .counter
  {
    color: #ffa34f;
  }

  .inventory-item-info:hover
  {
    border: 1px solid #ccc;
    background-color: #111;
    color: #07909a;
  }

  .inventory-item-info:hover a
  {
    text-decoration: none !important;
  }

  .sod_select
  {
    background-color: #222;
    color: #ccc;
    font-weight: 300;
  }

  .sod_select:hover
  {
    border: 1px solid #07909a;
  }

  .sod_select .sod_list_wrapper
  {
    background-color: #222;
  }

  .sod_select .active, .sod_select .selected
  {
    color: #73bdec !important;
    font-weight: 300 !important;
    background-color: #111 !important;
  }

  .header-right-toolbar-box
  {
    background-color: #222;
    color: #ccc;
  }

  .header-right-toolbar-box strong.blue
  {
    font-weight: 300;
    color: #ccc !important;
  }
  
  /* Villagers */
  .fur-residents-item-info, .fur-residents-item-info .durability
  {
    font-weight: 300;
    background-color: #242424 !important;
  }
  
  /* Forums */
  .forum td
  {
    background-image: none !important;
    background-color: #242424 !important;
    border: 1px solid #ccc;
    color: #ccc;
  }

  .forum-category
  {
    border-bottom: none !important;
  }

  .forum tr:hover td:not(.forum-category)
  {
    background-image: none !important;
    background-color: #111 !important;
  }
  
  /* Forum Posts */
  code
  {
    background-color: #111;
  }

  .quote-body
  {
    background-color: #111 !important;
    color: #ccc;
    border: 1px solid #ccc;
  }

  .thread-user-post div
  {
    color: #aaa !important;
  }
  

  
  /* Townhall */
  .jobs li a
  {
    background-color: #242424;
    border: 1px solid #ccc;
  }

  .jobs .counter
  {
    color: #ccc !important;
    font-weight: 300;
    border: 1px solid #444;
  }

  .jobs li a:hover .counter
  {
    border: 1px solid #444;
  }

  .jobs li a:hover
  {
    border: 1px solid #ccc;
    background-color: #111;
  }

  .town-hall-bottom .mayor-announcements li
  {
    background-color: #242424;
    margin-top: 20px;
    margin-right: 20px;
  }

  .town-hall-bottom .mayor .widget-content
  {
    height: 224px;
  }

  h2
  {
    color: #ffa34f !important;
  }
  
  /* Tooltipster Info: https://i.imgur.com/evvE3zh.png 
   * Was able to target by dragging image outside of browser.
   * If you take this part, please credit me. 
   * It took a lot of time to figure out. ;; */
  .tooltipster-content
  {
    background-color: #242424;
    color: #ccc;
    border-radius: 15px;
    border: 1px solid #ccc;
    margin-top: -7px;
  }

  .tooltipster-base
  {
    background-color: #242424;
  }

  .tooltipster-arrow span
  {
    border: none;
  }

  .tooltipster-arrow span:after
  {
    bottom: 100%;
    left: 50%;
    border: solid transparent;
    content: " ";
    height: 0;
    width: 0;
    position: absolute;
  }

  .tooltipster-arrow span:after
  {
    border-color: rgba(136, 183, 213, 0);
    border-bottom-color: #ccc;
    border-width: 6px;
    margin-left: -6px;
  }
  
  /* Career */
  .registration-well, .animal-item-info, .construction-item-info
  {
    background-color: #242424 !important;
    color: #ccc;
    border: 1px solid #ccc;
  }

  .animal-item-info:hover, .construction-item-info:hover
  {
    border: 1px solid #ccc !important;
    background-color: #111 !important;
  }

  .status, .job-text p
  {
    color: #ccc !important;
  }

  .crafting-list-item-info
  {
    background-color: #242424;
  }

  label
  {
    color: #ffa34f !important;
    font-weight: 300;
  }

  .job-description
  {
    background-color: #242424;
    color: #ccc !important;
    border: 1px solid #ccc;
    border-radius: 10px;
  }

  .warrior-info-section
  {
    color: #ffa34f !important;
  }

  .warrior-view-sword:before, .warrior-view-shield:before
  {
    background-color: #242424 !important;
  }

  .color-thief-bg
  {
    background-color: transparent !important;
    border: none !important;
  }

  .explore-item, .progress-bar
  {
    background-color: #242424 !important;
  }

  .explore-item .color-thief-bg
  {
    display: none;
  }

  .progress-bar .progress
  {
    background-color: #3f539f !important;
  }

  .progress-text
  {
    color: #ccc !important;
  }

  .progress-bar
  {
    margin-bottom: 10px;
  }

  /* Have only on career page? */
  .content h1
  {
    color: #ffa34f !important;
    letter-spacing: 2px;
    margin-top: -2px;
  }
  
  /* Career popup */
  .modal-content
  {
    background-color: #242424;
  }

  .table tr:hover td
  {
    background-color: #111 !important;
  }

  .table tr td
  {
    background-color: #242424 !important;
    border: 1px solid #ccc;
    color: #ccc;
  }

  .table tr td b
  {
    font-weight: 300;
    color: #ffa34f;
  }

  .in
  {
    background-color: rgba(0,0,0,0.5);
  }

  .well
  {
    background-color: #242424;
    color: #ccc;
  }

  .modal-body p
  {
    color: #ccc;
  }

  .modal-body p b
  {
    font-weight: 300;
    color: #ffa34f;
  }

  .input
  {
    background-color: #242424 !important;
    color: #ccc;
  }

  .inventory
  {
    color: #ffa34f;
  }
  
  /* Chatroom */
  .chatroom-users li
  {
    background-color: #242424 !important;
    border: 1px solid #ccc;
    border-width: 0px 0px 1px 0px;
  }

  .chatroom-wrapper.gradient:before
  {
    background: none !important;
    border: 1px solid #ccc;
    border-width: 1px 0px 0px 0px;
    height: 1px;
  }

  .user-message
  {
    background-color: #242424 !important;
    border: 1px solid #ccc !important;
    color: #ccc;
    width: 430px !important;
  }

  .chatroom-wrapper
  {
    border: 1px solid #ccc;
    border-width: 0px 0px 1px 0px;
  }

  .user-avatar-wrapper .user-avatar
  {
    display: none;
  }

  .widget-header .counter
  {
    background-color: #242424;
    border: 1px solid #ccc;
    color: #ccc;
  }
  
  /* General */
  p
  {
    color: #ccc;
  }

  p strong
  {
    font-weight: 300;
    color: #ffa34f;
  }

  .search
  {
    border: 1px solid #ccc !important;
  }
  
  /* New Message */
  .form-control
  {
    background-color: #242424;
    color: #ccc;
  }

  .user-checker-preview span
  {
    color: #ccc;
  }
  
  /* User-posts (Help pages + ?) */
  .thread-user-post-middle
  {
    color: #ccc;
  }

  .thread-user-post-middle strong
  {
    color: #ffa34f;
    font-weight: 300;
  }

  .thread-user-post-middle table td.head
  {
    background-color: #242424;
    color: #ffa34f;
    font-weight: 300;
  }

  .thread-user-post-middle table
  {
    margin-top: 10px;
  }

  .thread-user-post-middle table td.head font
  {
    color: #242424;
  }

  .thread-user-post-middle table td
  {
    border: 1px solid #ccc !important;
  }

  .margin-2em
  {
    color: #ffa34f;
  }
  
  /* Forum post bold and color */
  .thread-user-post-middle
  {
    font-weight: 300 !important;
  }

  .thread-user-post-middle font[color]
  {
    color: #ffa34f !important;
  }
  
  /* Giving Tree Page */
  div
  {
    color: #ccc;
  }

  .alert
  {
    background-color: #242424 !important;
  }

  .alert p
  {
    color: #ffa34f !important;
  }

  .panel
  {
    background-color: #242424;
  }

  /* Navigation Dropdown */
  .dropdown-menu
  {
    background-color: #242424;
    border: 1px solid #ccc;
  }

  header nav .dropdown-menu a
  {
    color: #07909a !important;
  }

  header nav .dropdown-menu a:hover
  {
    background-color: #111 !important;
    color: #ccc !important;
  }

  .divider
  {
    background-color: #ccc !important;
  }
  
  /* Protection page */
  .btn.protection-quotient
  {
    background-color: #242424 !important;
    color: #ffa34f !important;
    border: 1px solid #ccc !important;
  }

  .btn.protection-quotient span
  {
    margin-top: 10px;
  }
  
  /* User Settings */
  .gender-radio label
  {
    background-color: #242424 !important;
  }
  
  /* Item Museum Search */
  .select2-selection--single, .select2-dropdown, input.select2-search__field
  {
    background-color: #242424 !important;
  }

  .select2-selection__placeholder, .select2-dropdown, .select2-selection__rendered, input.select2-search__field
  {
    color: #ccc !important;
  }

  .select2-selection__arrow b
  {
    border-color: transparent transparent #ccc transparent !important;
  }

  .select2-results__option--highlighted[aria-selected], .select2-results__option[aria-selected=true]
  {
    background-color: #000 !important;
    color: #fff;
  }
  
  /* Costume Museum */
  .costume-museum-options .combo-select
  {
    background-color: #242424;
  }

  .comboslider-title
  {
    font-weight: 300;
  }

  .comboslider-prev:before
  {
    border-color: transparent #ccc transparent transparent;
  }

  .comboslider-next:before
  {
    border-color: transparent transparent transparent #ccc;
  }
  
  /* Paintie Gallery */
  .paintie-gallery .paintie
  {
    background-color: #242424 !important;
    border-color: #ccc !important;
  }

  .paintie-gallery .paintie:hover
  {
    background-color: #111 !important;
    border-color: #ccc !important;
  }
  
  /* Fur Idol */
  .fur-idol-prize
  {
    background-color: #242424 !important;
    border-color: #ccc !important;
  }
  
  /* Crafter Job Hover */
  .crafting-list-item-info:hover
  {
    background-color: #111 !important;
    border-color: #ccc !important;
  }

  .crafting-list-item-info
  {
    border-color: #ccc !important;
  }

  /* May 2017 : Update */
  .left-column > :first-child
  {
    height: 0px !important;
  }

  .right-column > :nth-child(2) .forum-posts li
  {
    background: none;
  }

  .left-column .notifications > thead
  {
    background: rgba(255,255,255,0.1) !important;
  }

  .left-column .notifications > thead > tr
  {
    border: none;
  }

  #logo a
  {
    background: url('https://i.imgur.com/wcmWhWg.png') no-repeat;
  }
}

@-moz-document regexp("https://www\\.furvilla\\.com/message.*")
{
/* Fixes for messages */


  .user-message
  {
    width: 500px !important;
    margin-top: 20px !important;
  }

  .user-avatar-wrapper .user-avatar
  {
    width: 75px;
    height: 75px;
    background-color: #242424;
    display: inherit;
  }

  .user-avatar-wrapper .text-center
  {
    min-width: 50px;
  }

  .user-avatar-wrapper
  {
    margin-top: 25px;
  }

  small
  {
    padding-bottom: 10px;
    color: #ccc !important;
  }

  #create-message textarea
  {
    width: 90% !important;
    margin-left: 5% !important;
  }

  .breadcrumbs + h1 + .widget + .widget-content
  {
    padding-bottom: 40px;
  }

  .breadcrumbs + h1 + .widget
  {
    margin: 0 !important;
  }

  .messages-view
  {
    border: 1px solid #ccc;
    border-width: 0px 0px 1px 0px;
  }
}

@-moz-document url-prefix("https://www.furvilla.com/forums")
{
  .thread-user-info p
  {
    color: #242424 !important;
  }

  .thread-user-info a, .thread-user-info
  {
    text-shadow: #fff 0px 0px 2px, #fff 0px 0px 2px, #fff 0px 0px 2px, #fff 0px 0px 2px, #fff 0px 0px 2px, #fff 0px 0px 2px, #fff 0px 0px 2px, #fff 0px 0px 2px, #fff 0px 0px 2px, #fff 0px 0px 2px, #fff 0px 0px 2px, #fff 0px 0px 2px, #fff 0px 0px 2px !important;
    font-weight: bold;
  }
}
