css:
@-moz-document url-prefix("https://login.yahoo.com/") {
body.resp {
    background-color: #0c0b0b;
}
.orko {
    letter-spacing: normal;
}
.orko, body {
    background: #0c0b0b;
    color: #878787;
    font-family: 'Helvetica Neue',Helvetica,Arial;
    letter-spacing: .5px;
    width: 100%;
    max-width: 100%;
}

*, ::after, ::before {
    box-sizing: border-box;
}
*, ::after, ::before {
    box-sizing: border-box;
}

img{
  filter: invert(0.6) sepia(5) hue-rotate(10deg) saturate(3%);
}


.login-header {
    line-height: 84px;
    padding-left: 50px;
    background-color: #0c0b0b;
    box-shadow: 0 0 6px #000;
    color: #26282a;
}


.login-box {
    box-sizing: border-box;
    box-shadow: 0px 1px 3px 2px rgba(0, 0, 0, .7);
    width: 360px;
    right: 10px;
    min-height: 550px;
    z-index: 1;
    padding: 0 5px;
    border-top: 1px solid #000;
}
.info-box, .login-box {
    position: absolute;
    top: 11px;
}
.login-body h1, .login-box, body.narrow {
    color: #fff;
}
.login-box, body.narrow {
    background-color: #0c0b0b;
}

.login-footer {
    text-align: center;
    padding: 6px 0;
    font-size: .58824em;
    position: fixed;
    bottom: 0;
    background: #0c0b0b;
    color: #26282a;
    z-index: 1;
    width: 100%;      
    }

.mbr-desktop-hd {
    position: relative;
    background-color: #0c0b0b;
     box-shadow: 0 0 6px #000;
    color: #26282a;
    line-height: 84px;
    padding-left: 50px;
}



.login-box .mbr-login-hd {
    padding: 40px;
    background-color: #0c0b0b;
    color: #cecece;
}
.loginish {
    min-height: 100%;
    background-color: #0c0b0b;
    color: #939393;
}
}
