css:
@-moz-document url-prefix("https://canvas.asu.edu"), domain("instructure.com") {
:root {
	--main-bg-color: #232323;
	--main-txt-color: #c9c9c9;
	--main-lnk-color: #419dbded;
	--main-row-highlight-color: #404040ba;
	--main-dark-highlight-color: #1a1a1a;
}

h1 {
	color: #c1c0c0 !important;
	font-weight: bold !important;
}

body {
	background-color: var(--main-bg-color);
	color: var(--main-txt-color) !important;
}

/* Main Courses Linking Workpath */
#breadcrumbs {
	background-color: var(--main-bg-color);
}

#breadcrumbs > ul,
#breadcrumbs > ul > li {
	background-color: var(--main-bg-color);
}

#breadcrumbs > ul > li + li:last-of-type a:hover {
	color: rgb(126, 27, 58);
}
#breadcrumbs > ul > li + li:last-of-type a {
	color: var(--main-lnk-color)
}

#custom-asu-header-ul li a:hover {
	background-color: var(--main-bg-color);
}
.header-bar {
	background-color: var(--main-bg-color);
}
.item-group-condensed .ig-row {
	background-color: var(--main-bg-color);
}
.module-sequence-footer .module-sequence-footer-content {
	background: #444343;
	background-color: var(--main-bg-color);
}

#custom-asu-header-ul li a currentcolor,
#custom-asu-header-ul li a:hover,
#custom-asu-header-ul,
.list-view > nav > ul > li > a,
.list-view > ul > li > a {
	color: var(--main-lnk-color);
	text-decoration-color: #419dbd;
}
/* Syllabus */
table.summary td,
table.summary tbody th {

	background-color: #151515;
}

#syllabus tr.date.date_passed td,
#syllabus tr.date.date_passed th {
	background-color: #232323;
}

.quiz-submission,
.question_editing {
	background: #515151;
	border: 1px solid #ffffffc4;
}

#syllabus tr.date.related td {
	background-color: #404040ba;
}

.toolbarView .headerBar {
	background: black;
}

.ig-list .ig-row {
	color: #f0f0f0;
}
.ig-list .ig-row a.ig-title:hover,
.ig-list .ig-row a.ig-title:focus {
	color: rgb(126, 27, 58);
}
.ig-list .ig-row a.ig-title {
	color: #fafafa80;
}

.ig-list .ig-row:hover {
	background-color: var(--main-row-highlight-color);
}

.ig-header .name {
	color: #848484e3;
	text-shadow: 1px 1px 0 #000;
}

.item-group-container {
	background: #0202027a;
}
.btn {
	color: #303030;
	background-color: #f3f3f3e0;
}
.btn:hover {
	color: var(--main-lnk-color);
	background: #fffffff5;
}

a {
	color: var(--main-lnk-color);
}

table {
	background-color: transparent;
}

.pages.show .course-title {
	display: none;
	font-size: 1.9em;
	font-weight: bold;
	color: #c4c4c4;
}


.fNHEA_blJt {
	color: #ffffffde;
}


.accessible-toggler {
	color: #b9b9b9;
	text-shadow: 1px 1px 0 #000;
}
.accessible-toggler:hover,
.accessible-toggler:focus {
	color: rgb(126, 27, 58);
}

.czbXA_bGBk {
	background-color: var(--main-bg-color);
}
#content_container {
	background-color: #000000f0;
}

.ic-Dashboard-header__layout {
	background-color: var(--main-bg-color);
}

.root_2rqW {
	background-color: var(--main-bg-color);
}
button.eHiXd_dnnz[type="button"] {
	color: #bababab3;
	background-color: var(--main-bg-color);
}

.ic-notification__content {
	background: #070707a3;
}

button.eHiXd_bGBk[type="button"] {
	color: #ffffffab;
	background-color: #1111116e;
}

.root_3KEc {
	color: #ffffffbd;
	background: var(--main-bg-color);
}
.bFgzf_bGBk {
	background-color: var(--main-bg-color);
}
.ELpHc_bGBk.ELpHc_bLsb {
	color: #ffffffc2;
}

.ELpHc_bGBk {
	color: #ffffffbd;
}


/* Discussion */
.discussion_entry {
	background: #0d0d0d;
}
.replies {
	border-left: solid #404040 16px;
}

.pinned-discussions-v2__wrapper {
	border: 1px solid #C7CDD1;
	background-color: #1a1a1a;
}

/* CALENDER */
#calendar-app .fc-month-view .fc-body,
#calendar-drag-and-drop-container .fc-month-view .fc-body {
	background-color: #ffffff1c;
}

#calendar-app .fc-month-view .fc-widget-header {
	color: #ffffffd9;
}

#right-side .events_list .event-details::after,
#right-side .events_list .todo-details::after,
#right-side .to-do-list .event-details::after,
#right-side .to-do-list .todo-details::after {

	background: linear-gradient(to right, var(--main-bg-color) 0%, #ffffff54 80%);
}

#context-list-holder,
#undated-events {
	background-color: #00000040;
	max-height: 300px;
	overflow-y: auto;
}

#minical {
	background-color: #00000040;
	text-shadow: none;
	border: 1px solid #C7CDD1;
}
#minical .fc-toolbar h2,
#minical .fc-toolbar .h,
#minical .fc-basic-view td.fc-day-number {
	color: var(--main-txt-color)
}

.fTsnK_eXrk {
	color: #577488;
}

/* RUBRIC */
.rubric_container thead th,
.rubric_container .rubric_title {
	background-color: #7a7979a1;
}
.ui-widget-content a {
	color: var(--main-lnk-color)
}
.mce-menubtn span,
.ui-widget-content {
	color: var(--main-txt-color);
}

/* Peer Review Popup */
.ui-dialog .ui-dialog-titlebar.ui-widget-header,
.ui-dialog .ui-datepicker .ui-widget-header.ui-datepicker-header,
.ui-datepicker .ui-dialog .ui-widget-header.ui-datepicker-header {
	background: #000000e8;
	padding: 16px 12px;
	border-bottom: 1px solid #C7CDD1;
}
.ui-widget-overlay {
	background: rgba(47, 48, 49, 0.75);
}

/* Discussions */
.ic-discussion-row,

button.eHiXd_bGBk[type="button"] {
	background-color: var(--main-bg-color);
}
.czbXA_bGBk {
	color: var(--main-lnk-color);
	background-color: var(--main-bg-color);
}
.MlJlv_ebWM {
	color: #667f91;
}
.discussions-v2__container-image {
	background-color: var(--main-bg-color);
}
.closed-for-comments-discussions-v2__wrapper,
.unpinned-discussions-v2__wrapper {
	background-color: var(--main-dark-highlight-color);
}

/* Assignments */
.file-upload-submission {
	background: #232323;
}
.submission-details-comments .comments {
	background-color: #131313;
}

select.ic-Input,
textarea.ic-Input,
input[type="text"].ic-Input,
input[type="password"].ic-Input,
input[type="datetime"].ic-Input,
input[type="datetime-local"].ic-Input,
input[type="date"].ic-Input,
input[type="month"].ic-Input,
input[type="time"].ic-Input,
input[type="week"].ic-Input,
input[type="number"].ic-Input,
input[type="email"].ic-Input,
input[type="url"].ic-Input,
input[type="search"].ic-Input,
input[type="tel"].ic-Input,
input[type="color"].ic-Input,
.uneditable-input.ic-Input {
	background-color: #b7b7b7;
}

/* Messages / Inbox */
.conversations .panel {
	background: #171717;
}

element {
	background-color: #646464 !important;
}
span {
	color: #949494 !important;
	font-weight: bold !important;
}

/* Zoom */
#content_container {

    background-color: #131313;

}
.nav-tabs,
.nav-tabs {
    border-bottom-width: 2px;
    border-bottom-color: #f7f7fa85 !important;
}

/* YellowDig */
.bg--gray-1, .yd .yd-board__tab-content {

    background-color: #232323 !important;

}
}
