@-moz-document url-prefix("https://www.rmf24.pl") {
body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #fc0 #1c1c1c !important;
}
input[type="text"],input[type="search"],input[type="email"],input[type="password"],textarea,select{
    background: #1c1c1c !important;
    color: #fff !important;
    border: 1px solid #fc0 !important;
}
.navigationMenuMain .menuLeft,#twojezdrowieCont .page3{
    background: #1c1c1c;
}
.navigationMenuMain .menu a span,.sluchajRadia .sluchajR,.article .article-title,.forum__header-label-text,.frontPage .mixerObjectListCategory.mixerListMain .boxHeader, .frontPage .mixerObjectListCategory.mixerListMain .boxHeader h1,#twojezdrowieCont a,.frontPage .contentBlock h3 a,.articleEmbedAudio .boxHeader h2,.embed .boxHeader h2,.najwazniejszeArt .title a,.footerNavigation .footTitle{
