}
.playYellow{
    background: url(https://www.rmf.fm/2018/img/blue-yellow-play-x2.png) no-repeat center center;
    background-size: 35px;
}
a, a:link, a:active, a:visited,.breadcrumbs-item strong,.article .article-lead,.articleContent p,.embed-subheader-text,.trwaLadownaie,.najwazniejszeArt .title a:hover,.article .article-date{
    color: #fff;
}
.autorzy .autinfo,.article-footer .article-source,.elementTagsListBox .elementTagsListTitle{
    color: #1c1c1c;
}
.mixerObjectListCategory .mixerListMain #readMore .wiecej{
    background: #fc0;
    color: #1B3481;
}
.xsound b{
    color: #fff;
}
.xsound small{
    color: #fff;
}
.podcast-subscribe-block .sub,.articleContent{
    color: #fff;
}
.podcast-subscribe-block .subscribe-link{
    background: #1c1c1c no-repeat 8px center;
    background-size: 32px 32px;
}
#commentsFB .loaderComment img{
    display: none;
}
.commonCopyright .boxBody p:after{
    content: " | Ciemny motyw by Maciejozaman";
    color: #fc0;
}
.articleEmbedArticle,.articleEmbedArticleListTitle .boxHeader,.articleEmbedArticleListTitle .boxBody,.reportMenu .boxBody .list ul,.mainExtraBanner .noPhoto,.dropdown-menu{
    background: #1c1c1c;
}
.background-black,.grayFooter{
    background: #1c1c1c !important;
}
.reportMenu .boxBody .list li a{
    background: #fc0;
}
.nav-tabs > li > a{
    border-radius: 0;
}
.navigationMenuMain .menu li ul li a:hover{
    background: #1B3481;
}
.links_bar .search .szukajButton{
    background-color: #fc0;
}
.links_bar .search .szukajButton:after{
