css:
/* i really want this to be global */
.p-header {
    background: #ed8106;
    padding-top: 16px;
    padding-bottom: 16px;
}
.p-nav {
    background: #ed8106;
    transition: ease-in .15s all;
}
.p-sectionLinks {
    border: 1px solid rgb(204, 204, 204);
    font-size: 1.3rem;
    color: #b5b9c0;
    background-color: rgb(240, 240, 240);
    border-bottom: 1px solid #484d56;
    transition: ease-in .15s all;
}
a {
    color: #ed8106;
    text-decoration: none;
}
.uix_extendedFooter {
    background: #ed8106;
    border-top: 0 solid #fff;
    padding-top: 48px;
    padding-bottom: 48px;
}
.p-footer-inner {
    padding-top: 10px;
    padding-bottom: 10px;
    background: #ed8106;
}
.p-footer-copyrightRow {
    color: #082437;
    background: #ed8106;
    border-top: 1px solid #fff;
    padding-top: 16px;
    padding-bottom: 16px;
}
.p-nav-scroller {
    background: #2196f3;
    background: linear-gradient(to right, rgba(33, 150, 243, 0) 0%, #ed8106 33%);
}
.hScroller-action {
    background: #2196f3;
    background: linear-gradient(to right, rgba(33, 150, 243, 0) 0%, #ed8106 33%);
}
.hScroller-action--end {
    background: #2196f3;
    background: linear-gradient(to right, rgba(33, 150, 243, 0) 0%, #ed8106 33%);
}
.uix_headerContainer .p-navSticky.is-sticky .p-nav {
    background: #ed8106;
    border-radius: 0 !important;
}
.p-nav-scroller .hScroller-action.hScroller-action--end {
    background: #ed8106;
}
.p-nav-scroller .hScroller-action.hScroller-action--start {
    background: #ed8106;
}
