css:
@-moz-document domain("forum.onligamez.ru") {
    #logo, .PanelScroller, .titleBar, .siropuChat_h3, footer, .uSocial-Share, #siropuChatHeader, .sidebar, #Block1st, .home, .pageNavHeader {
        display: none !important;
    }
    html#XenForo {
        background: url(https://creative-moscow.ru/assets/images/oz.png) no-repeat #1D1E22;
        background-size: cover;
        min-height: 100%;
        background-attachment: fixed;
    }
    #header {
        background-image: none;
    }
    .xenForm {
        color: white;
    }
    #moderatorBar {
        background: none;
        border: none;
    }
    .nodeList .categoryStrip {
        background-color: #333641;
    }
    .redactor_box .redactor_smilies {
        background: #333641;
        padding: 5px;
    }
    .OverlayCloser {
        color: white;
    }
    .redactor_box .redactor_smilies .tabs {
        padding-bottom: 3px;
    }
    .pageContent .breadcrumb .crust .arrow {
        display: none;
    }
    #siropuChat, .siropuChatMessages, .siropuChatUsers, #siropuChatPrivateUsers, .breadcrumb {
        background: #1D1E22;
        border-right: none;
    }
    .pageContent .breadcrumb .crust .arrow {
        display: none;
    }
    .siropuChatTagged {
        background-color: #333641;
        padding: 5px;
        border-radius: 0px;
    }
    .pageContent .breadcrumb .crust a.crumb {
        background: #333641;
        color: white;
        border: none;
    }
    .pageContent .breadcrumb {
        height: 40px;
        border: none;
    }
    .mainContent {
        margin-right: 0px;
    }
    #content .pageContent {
        background: #1D1E22;
    }
    .sectionMain #siropuChat {
        border: 0px;
    }
    #siropuChatEditor {
        border-radius: px;
    }
    .sectionMain {
        border-radius: 0px;
        padding: 0px;
        border: none;
    }
    .siropuChatUsers a {
        color: orange;
    }
    .siropuChatMessage, .siropuChatMessage a {
        color: #ddca7e;
    }
    .siropuChatContentLeft {
        color: #9a8297;
        font-size: 16px;
        font-family: Roboto, 'Consolas', 'Tahoma';
        overflow: hidden;
        margin-right: 75px;
    }
    .siropuChatUsers>li {
        margin-bottom: 10px;
    }
    .siropuChatBot .siropuChatMessage {
        color: #96b38a;
    }
    .avatar img, .avatar .img, .avatarCropper {
        border: none;
        background: none;
        padding: 0px;
    }
    #siropuChat .username {
        color: #809bbd;
    }
    .siropuChatMessages .avatar img {
        width: 35px !important;
        height: 35px !important;
        border-radius: 0px;
    }
    .siropuChatMaximized #siropuChatContent {
        height: 720px;
    }
    .bbCodeQuote .quoteContainer, .bbCodeBlock .type {
        background: none;
        border-bottom: none;
    }
    .bbCodeBlock {
        border: 1px dashed white;
    }
    .siropuChatUsers .avatar img {
        width: 10px;
        height: 10px;
    }
    .siropuChatUsers, #siropuChatPrivateUsers {
        width: 130px;
    }
    #siropuChatEditor {
        background: none;
        border-top: none !important;
    }
    .internalLink:link {
        color: #a7925a;
    }
    #siropuChatEditor .button {
        box-shadow: none;
        border-radius: 0px;
    }
    #siropuChatEditor .textCtrl {
        background: none;
        border: 0px;
        border-bottom: 1px solid white;
        border-radius: 0px;
        color: white;
    }
    #Block1st, #Block2nd {
        background-color: #1D1E22;
        border: none;
    }
    #Block1st, #Block2nd a {
        color: #ddca7e !important;
        border: none;
    }
    .VietXfAdvStats_SectionItem {
        background: none
    }
    #Block2nd {
        margin-left: 0px;
    }
    #Block2nd .tabs, #Block1st .tabs {
        border: none;
        margin-bottom: 10px;
    }
    .tabs li a, .tabs.noLinks li {
        background: none;
        border: none;
        color: white;
    }
    .tabs {
        border-bottom: none;
    }
    .PageNav a.text, .PageNav a {
        color: white;
    }
    .VietXfAdvStats #Block1stPanes, .VietXfAdvStats #Block2ndPanes {
        border: none
    }
    .VietXfAdvStats h3 {
        background-color: #1D1E22
    }
    .XenBase .navTabs .navTab.selected .tabLinks {
        background-color: #1D1E22
    }
    .PageNav a {
        background: none;
        border: none;
    }
    #siropuChatSmilies .tabs.Tabs li a {
        color: black;
    }
    #siropuChatSmilies .tabs.Tabs li.active a {
        color: red;
    }
    #siropuChatContent {
        height: 700px;
    }
}
