css:
@-moz-document url-prefix("https://trashbox.ru/") {
body {
    background: #000;
    color: #fbeee4 !important;
}
.h_tags_menu_top {
    color: #fbeee4 !important;
}
.span_item_active {
    background: #333333;
}
header {
    background: rgba(0, 0, 0, 0.8) !important;
}
#img_top_logo {
    filter: invert(1);
}
span {
    color: #fbeee4 !important;
}
.div_layout_content {
    background: #131313;
}
.div_topic_cover_caption {
    color: #fbeee4;
}
.div_panel_bordered_caption {
    background: #222;
    color: #fbeee4 !important;
}
.div_panel_bordered {
    background: #111;
}
.div_panel_bordered_caption {
    border-left: 5px solid #222;
    border-right: 5px solid #222;
}
.div_panel_bordered {
    border: 1px solid #333;
}
.div_cell > span {
    color: white !important;
}
.div_right_rec_text {
    background: #111;
    border: 1px solid #333;
}
.div_panel_gray_caption {
    color: gray;
}
.div_panel_bordered_50 {
    background: #222;
}
.div_topic_day > span {
    color: white !important;
}
#div_top_logo .cell2, #div_top_logo:hover .cell2 {
    background: #0a70a455;
}
#div_top_logo a:hover {
    background: #042e43 !important;
}
#div_top_menu_text a:hover {
    color: white!important;
    background: #006080!important;
}
#div_top_menu_text a.current {
    color: white!important;
    background: #006080!important;
}
a, a:visited {
    color: #007da7;
}
.div_index_topics_more {
    border: none;
    background: #006080!important;
}
.page_index .div_timeline_topics:before {
    border-top: 1px solid #333;
}
#div_push_notify_button_postload {
    background: #006080!important;
}
.div_topic .div_text {
    color: #fbeee4;
}
.div_topic .div_text h1, .div_topic .div_text h2, .div_topic .div_text h3 {
    color: #fbeee4;
}
.div_line_header_text {
    background: black;
    filter: invert(1);
}
.div_line_header {
    filter: none;
}
.div_comment {
    border-left: 5px solid #333333;
}
.div_comment .div_theader {
    background-color: #333333;
}
.h_left_header {
    background: #333333;
}
.div_forum_dark div {
    background: #222;
}
.div_cell > a {
    filter: invert(1);
}
.div_forum_answer > .a_forum_answer {
    filter: invert(0);
    background: #006080!important;
}
.div_tags_menu_top .a {
    color: white!important;
    background: #006080;
}
.div_gallery_preview_block.text_gallery {
    outline: 5px solid #333333;
    background: #333333;
}
div.center.gallery {
    outline: 5px solid #333333;
    background: #333333;
}
.div_file_download_button2.button {
    background: #333333;
    color: #1fb6f2;
    border-bottom: 1px dashed #1fb6f2;
}
.div_file_download_button2.info {
    background: #333333;
    color: #1fb6f2 !important;
}
.div_file_download_button1.info {
    color: #fbeee4!important;
    background: #222;
    border: 1px solid #333;
}
.div_file_cert_green {
    background: #004a00!important;
}
.div_file_av3 {
    background: #821818!important;
}
.div_landing_rec:before, .div_topic_rec:before {
    background: black;
}
.div_topic_rec {
    border: 1px solid #333;
}
#div_show_more_comments {
    background: #006080;
    color: #fbeee4;
}
#div_modal {
    background: black !important;
}
#div_auth_new_conditions {
    background: #222 !important;
}
input:-internal-autofill-selected {
    background-color: #333 !important;
    color: rgb(0, 0, 0) !important;
}
.div_auth > .center > a > img {
    filter: invert(1);
}
.div_form_accept_rules {
    background: #222;
}
.input_register {
    border: 1px solid #333;
    color: #fbeee4;
}
.div_top_black_buttons {
    background: #006080;
}

#div_user_menu {
    background: #555;
}
#div_user_menu > a {
	color: #fbeee4;
}

#div_user_menu a:hover {
    color: #fbeee4;
    background: #006080;
}

.div_panel_no1 {
    border-right: 20px solid #222;
}
.div_panel_no3.div_site_info {
    background: #222;
}

#div_bottom_panel {
    background: #222;
}

.div_panel_view {
    background: #222;
}

.div_panel_no2 {
    border-right: 10px solid #222;
    border-left: 10px solid #222;
}
.div_panel_no3 {
    border-left: 20px solid #222;
}

#div_user_menu a, #div_user_menu a:visited {
    color: #fbeee4;
}

#div_notify {
    background: #222;
}

input {
    background: #333 !important;
	border: none;
    border-radius: 8px;
    padding: 3px;
    margin: 2px 0px;
    color: #fbeee4;
}

.div_editor_resize {
    background: none;
}
.carret {
    border-left: 6px solid transparent;
    border-right: 6px solid transparent;
    border-top: 5px solid rgba(255,255,255,.5);
}

::-webkit-scrollbar-track
{
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,0.3);
	border-radius: 0px;
	background-color: transparent;
}

::-webkit-scrollbar
{
	width: 4px;
	background-color: transparent;
}

::-webkit-scrollbar-thumb
{
	border-radius: 0px;
	-webkit-box-shadow: inset 0 0 6px rgba(0,0,0,.3);
	background-color: #006080!important;
}

#div_new_comment_check {
    background: black;
}

.zero_space_padding2 {
	background: #006080!important;
    box-shadow: 0 0 0 1px #006080!important;
    -webkit-box-shadow: 0 0 0 1px #006080!important;
}

.div_index_highlight_block .div_topic_cover_caption {
	border-left: 8px solid #006080!important;
}

.div_topic_cover.reviews .div_topic_cover_caption {
    border-left: 9px solid #006080!important;
}

.h_field {
    background: #006080!important;
}

#frm_topic > div.div_form_block > table > tbody > tr:nth-child(2) > td:nth-child(1) {
	background: #006080!important;
}

#frm_topic > div.div_form_block > table > tbody > tr:nth-child(2) > td:nth-child(2) > div {
	background: #006080!important;
}

#div_add_files > div.comp_left {
	background: #006080!important;
    color: white;
}

#div_add_files > div.comp_left > b > a {
    color: white;
}

#div_add_files > div.comp_left > b > a:before {
    content: "[+] ";
    color: white;
}

#div_menu_page {
    background: rgba(0, 0, 0, 0) !important;
}
  
#div_top_news_row, .div_top_news_b1,.div_top_news_b2  {
    background: black;
}

#div_anim_rec_before_files .div_topic_caption {
    border-left: 9px solid #006080 !important;
}

.div_comment.highlighted_comment {
	border-left: 5px solid #c38585;
}

blockquote {
    background: #222;
}

.div_comment {
	border-left: 5px solid #333333;
}

.div_comment {
	border-left: 5px solid #333333;
}

.div_topic.unread3 {
    background: none !important;
}

.editor_area {
    color: black;
    background: #aaa;
}

#div_editor_comments > div.div_editor_controls_block {
	background: #006080!important;
    margin: 2px;
}

.div_editor_button {
    background: #006080!important;
    width: 37px;
}
.div_editor_button:hover {
    background: #333333!important;
}

.div_editor_controls .div_editor_button {
    width: 37px;
    text-align: center;
}

.div_editor_button img {
    width: 16px;
    height: 16px;
    border: none;
}


.div_comment .div_content_comm {
    color: #fff;
}

@media screen and (min-width: 990px) {
    body.body_branding {
		background-image: none !important;
        background-color: #222;
    }

}

@media screen and (min-width: 990px) {
    body.body_branding {
        background-color: #222;
    }
}

    #a_branding_top2 {
		display:none;
    }

.body_branding .div_branding_top {
    display:none;
}
}
