css:
@-moz-document domain("discord.com") {
/*hover == false*/
[class^="toolbar-"]{
    max-width: 40px;
    transition: max-width 1.5s;
    -webkit-transition: max-width 1.5s;
    -moz-transition: max-width 1.5s;
    -o-transition: max-width 1.5s;
    height: 100%;
}
[class^="toolbar-"]::before{
    content: "ï¸™";
    font-size: 24px;
    margin: 0 8px;
}
/*hover == true*/
[class^="toolbar-"]:hover{
    max-width: 100vw;
}
[class^="toolbar-"]:hover::before{
    content: "<";
    font-size: 24px;
    margin: 0 8px;
}
}
