css:
@-moz-document domain("twitch.tv")
{
.front-page-carousel, .blue-bar {display:none;}

.channel-root {
    padding: 0.4rem;
}

.qa-theatre-mode-button{
    position: absolute;
	bottom: 150%;
    width: 200%;
	height: 2500%;
	cursor:copy!important;}

.tw-root--theme-dark .channel-info-bar {
background: rgb(15,14,17);
background: linear-gradient(0deg, rgba(15,14,17,0) 5%, rgba(25,23,28,1) 30%);
    border:none!important;
}

.player-slider__buffer{background: black;}
.player-slider__left {
    background: #6441a4;}


.pl-controls-top, .pl-controls-bottom, .player-play-overlay
{     background: none;
}


.pl-pinned-item, .player-streaminfo, .player-streamstatus
{    display: none;
}

.pl-clips-button {
filter: invert(100%);
  position: absolute;
  right: 28px;}

span.chat-author__display-name {
    color: red !important;
    letter-spacing: 1.2px !important;
    font-size: 12px;
       
}

.tw-justify-content-start p {font-weight: bold; font-size: 11px!important;}
.tw-hide.tw-pd-x-2.tw-xl-flex {padding-left: 0rem!important;}


.tw-button--hollow
  {
 display: none;
  }

.top-nav__search-container:not(:hover) {opacity:0.5;}

.top-nav__nav-link.tw-interactive:nth-child(5), .tw-align-self-center.tw-pd-x-1:last-child  {display:none!important;}

 .prime-offers, .tw-lg-flex, .tw-mg-x-1 .tw-relative button
  {
    display: none !important;
  }

.tw-line-height-body
  {
    font-weight: bold;
  }

.whispers-threads-box__container {
	opacity: 0 !important;
	transition: opacity 0.2s ease-in-out;
}

.whispers-threads-box__container:hover {
    opacity: 1 !important;
  }

.channel-leaderboard {
    	opacity: 0;
    	height: 0rem;
    	transition: opacity 0.2s ease-in-out;}

.channel-leaderboard:hover {
    	opacity: 1;}

.tw-tower--300>* {
    width: 25%!important;
}
}
