css:
@-moz-document regexp("moz-extension://8e129c46-0b04-4d69-a065-7f6f4ae62e5b.+") {

html, body {
	background-color: #282a36;
	color: #f3f3f3;
}

#header {
	border-color: transparent;
	box-shadow: 0 0 16px -3px #191A21;
}

#options input[type="number"] {
	border-width: 1px;	
}

input[type="number"],
select,
input[type="checkbox"],
button,
input:not([type]) {
	background-color: #282a36;
	border-color: #191A21 !important;
	color: #6272a4;
	background: none;
	border-radius: .2rem;
	transition: .12s all;
}

input[type="number"]:hover,
select:hover,
input[type="checkbox"]:hover,
button:not([disabled]):hover,
input:not([type]):hover {
	border-color: #6272a4 !important;
	background-color: #282a36 !important;
	color: #f3f3f3;
	cursor: pointer;
}

input:not([type]):hover {
	cursor: auto !important;
}

input[type="checkbox"] {	
	border-color: #191A21 !important;
}

svg.svg-icon.checked,
svg.svg-icon.select-arrow {
	fill: #6272a4;
}

#enabled-label:hover svg {
	fill: #f3f3f3 !important;
}

.cm-s-dracula .CodeMirror-activeline-background {
	background: #2d2e3b;	
}

.resize-grip {
	border-color: transparent;
	box-shadow: 0 0 15px -.5px #191A21;
	border-radius: 2rem !important;
}

.resize-grip::after {
	border: none;
}

.CodeMirror-focused,
.codeMirror>div>textarea {
	outline: none !important;
}

}
