css:
@-moz-document domain("be.hardware.info"), domain("nl.hardware.info") {
/* GENERAL */
body
{
    margin: 138px 0 0;
}

h3,
h4.product-table__title,
.showcase__header-title
{
    font-size: 18px;
}

.sitearea_content
{
    max-width: 1180px;
}

.leaderboard,
.sidebar_right_top
{
    margin-top: -40px;
}

.leaderboard
{
    margin-bottom: 5px;
    margin-top: -12px;
    margin-left: 209px;
}

.category
{
    font-size: 13px;
    border-radius: 5px;
}

/* HEADER */

#header
{
    margin-bottom: 0;
}

#header .header__container
{
    height: 120px;
}

#header.header--single-row .header__container
{
    height: 60px;
}

#header .header-logo img
{
    height: 36px;
}

#header.header--single-row .header-logo
{
    width: 72px;
}

#header.header--single-row .header-search
{
    margin-left: 500px;
}

#header .header-search input
{
    height: 32px;
}

#header .header-search .button
{
    width: 32px;
    height: 32px;
}

#header .header-search .button img,
#header #userinfo img
{
    width: 16px;
    height: 16px;
}

#header.header--single-row #navigation
{
    bottom: -5px;
}

#header #navigation .dropdown
{
    margin-right: 0;
}

#header #navigation .dropdown__title
{
    font-size: 16px;
}

#header .dropdown__avatar-wrapper:after
{
    top: 1px;
}

#header .dropdown__avatar-container .userinfo__title
{
    position: relative;
    bottom: 8px;
}

/* FRONTPAGE */
.frontpage .spotlight
{
    width: 820px;
    grid-template-columns: repeat(3, 1fr);
}

.frontpage .spotlight article:nth-of-type(3),
.frontpage .spotlight article:nth-of-type(5)
{
    display: none;
}

.frontpage .spotlight .spotlight__title
{
    font-size: 16px;
}

.frontpage .spotlight .spotlight__title--large
{
    font-size: 24px;
}

.frontpage .spotlight .spotlight__category
{
    margin-bottom: 0;
    font-size: 11px;
}

.frontpage .spotlight .spotlight__category.category--primary
{
    font-size: 13px;
}

.frontpage .spotlight .spotlight__details span,
.frontpage .frontpage__article--overview .overview-item--large .overview-item__details
{
    font-size: 15px;
}

.frontpage .tiles
{
    display: none;
}

.frontpage div:nth-of-type(2) .frontpage__sidebar
{
    margin-top: -296px;
}

.frontpage .frontpage__article--overview .overview-item--large
{
    padding: 8px;
}

.frontpage .frontpage__article--overview .overview-item--large h3
{
    font-size: 18px;
}

.frontpage .frontpage__article--overview .overview-item--large .overview-item__image
{
    max-height: 120px;
    overflow: hidden;
}

.frontpage .showcase .showcase__header
{
    padding: 12px;
}

.frontpage .showcase .showcase-item
{
    padding: 12px;
}

.frontpage .showcase .showcase-item__image-container
{
    margin-bottom: 0;
}

.frontpage .showcase .showcase-item__title,
.frontpage .showcase .showcase-item__subtitle,
.frontpage .showcase .showcase-link,
.frontpage .showcase .showcase-link span
{
    font-size: 15px;
    margin-top: 0;
    margin-bottom: 0;
}

/* ARTICLES */
.article h1
{
    font-size: 32px;
    margin-bottom: 0;
}

.article h2
{
    font-size: 24px;
}

.article .article__data,
.article .article__content p
{
    margin-bottom: 16px;
}

.article .article__data span
{
    font-size: 16px;
}

.article .article__content
{
    max-width: 740px;
    text-align: justify
}

.article .article__content__social-bar a
{
    width: 38px;
    height: 38px;
}

.article .article__content__social-bar .button
{
    height: 38px;
    font-size: 15px;
}

article.related .related__title
{
    font-size: 24px;
    margin-bottom: 0;
    padding-bottom: 0;
    border-bottom: none;
}

article.related .overview-block--item-wrapper.overview-block--item-wrapper--rows-of-3 .overview-item
{
    border: none;
    flex: 0 0 16.666666%
}

article.related .overview-block--item-wrapper.overview-block--item-wrapper--rows-of-3 .overview-item .overview-item__title
{
    font-size: 18px;
}

article.related .overview-block--item-wrapper.overview-block--item-wrapper--rows-of-3 .overview-item .overview-item__details *
{
    font-size: 16px;
    display: block;
}

.comments .comments_header
{
    padding: 0;
}

.comments .comments_header h2
{
    font-size: 28px;
}

.comments .comments_header #comment_sorter
{
    height: 36px;
    padding: 8px;
}

.comments .fos_comment_new_main_reply .tab__index
{
    font-size: 16px;
}

.comments .fos_comment_new_main_reply .tab__content
{
    padding: 0;
}

.comments .editor .editor__button
{
    width: 20px;
    height: 20px;
}

.comments .editor .fos_comment_submit input.button
{
    padding: 8px 14px;
}

.comments .fos_comment_comment_show div:first-of-type
{
    margin: 12px 0 8px;
}

.comments .fos_comment_comment_show .fos_comment_comment_body
{
    margin-bottom: 12px;
}

.comments .fos_comment_comment_show .fos_comment_comment_reply
{
    padding: 0 0 12px 45px;
}

.comments .fos_comment_comment_show .fos_comment_comment_reply button
{
    padding: 8px 14px;
}

.comments .fos_comment_comment_avatar img
{
    margin-right: 8px;
}

/* SIDEBAR */
.sidebar-item
{
    padding: 12px;
}

.sidebar-item .sidebar-item__title
{
    margin-bottom: 0;
}

.sidebar-item .sidebar-item__content
{
    border-top: none;
    border-bottom: 1px solid #d6d6d6;
}
}
