}

.nav-tabs.nav-justified>.active>a, .nav-tabs.nav-justified>.active>a:focus, .nav-tabs.nav-justified>.active>a:hover {
    border: 1px solid hsl(209, 13%, 35%);
    border-bottom: none;
}

.nav-tabs>li>a {
    border: none;
}

.nav-tabs.nav-justified>li>a {
    border-bottom: 1px solid hsl(209, 13%, 35%);
}

.jumbotron, .breadcrumb, .panel, .well, .dropdown-menu {
    background-color: hsl(230, 10%, 12%);
    box-sizing: border-box;
    border: 1px solid hsl(209, 13%, 35%);
    border-radius: 4px;
}

.pagination>.disabled>a, .pagination>.disabled>a:focus, .pagination>.disabled>a:hover, .pagination>.disabled>span, .pagination>.disabled>span:focus, .pagination>.disabled>span:hover, .pagination>li>a, .pagination>li>span {
    color: hsl(210, 80%, 70%);
    background-color: hsl(230, 10%, 12%);
    box-sizing: border-box;
    border: 1px solid hsl(209, 13%, 35%);
}

.pagination>li>a:focus, .pagination>li>a:hover, .pagination>li>span:focus, .pagination>li>span:hover {
    color: hsl(210, 80%, 90%);
    background-color: hsl(230, 10%, 18%);
    border: 1px solid hsl(209, 13%, 35%);
}

.pagination>.active>a, .pagination>.active>a:focus, .pagination>.active>a:hover, .pagination>.active>span, .pagination>.active>span:focus, .pagination>.active>span:hover {
    color: #f5ffff;
    background-color: hsl(230, 10%, 18%);
    border: 1px solid hsl(209, 13%, 35%);
}

.panel-default>.panel-heading, .panel-body {
    color: #f5ffff;
    background-color: hsl(230, 10%, 12%);
    box-sizing: border-box;
    border: none;
}

.col-sm-2 .btn {
    display: block;
    position: relative;
    margin-bottom: 5px;
    float: right;
}

.btn {
    background-color: hsl(230, 10%, 18%);
    border-color: hsl(209, 13%, 35%);
    color: #f5ffff;
}

.btn-danger {
    color: #fff;
    background-color: #d9534f;
    border-color: #d43f3a;
}

.btn-success {
    color: #fff;
    background-color: #5cb85c;
    border-color: #4cae4c;
}

.btn-default:hover, .btn-primary:hover, .btn-default:focus, .btn-primary:focus, .btn-default:active, .btn-primary:active, .btn:focus, .btn:hover, .btn-default.active.focus, .btn-default.active:focus, .btn-default.active:hover, .btn-default:active.focus, .btn-default:active:focus, .btn-default:active:hover, .open>.dropdown-toggle.btn-default.focus, .open>.dropdown-toggle.btn-default:focus, .open>.dropdown-toggle.btn-default:hover {
    background-color: hsl(230, 10%, 24%);
    border-color: hsl(209, 13%, 35%);
    color: #f5ffff;
}

td .btn {
    padding: 0px;
    background-color: inherit;
    border: none;
    color: hsl(210, 80%, 70%);
}

td .btn-danger {
    color: hsl(357, 95%, 65%);
}

td>.btn-danger:hover, .btn-danger:active, .btn-danger:focus {
    background-color: inherit;
    color: hsl(357, 95%, 85%);
}

.alert-info {
    background-color: hsl(230, 10%, 18%);
    color: #f5ffff;
    border: 1px solid hsl(209, 13%, 35%);
}

.table>tbody>tr.success>td, .table>tbody>tr.success>th, .table>tbody>tr>td.success, .table>tbody>tr>th.success, .table>tfoot>tr.success>td, .table>tfoot>tr.success>th, .table>tfoot>tr>td.success, .table>tfoot>tr>th.success, .table>thead>tr.success>td, .table>thead>tr.success>th, .table>thead>tr>td.success, .table>thead>tr>th.success {
    background-color: #142;
    color: #aeb;
}

.table>tbody>tr:nth-of-type(even).success>td, .table>tbody>tr:nth-of-type(even).success>th, .table>tbody>tr:nth-of-type(even)>td.success, .table>tbody>tr:nth-of-type(even)>th.success, .table>tfoot>tr:nth-of-type(even).success>td, .table>tfoot>tr:nth-of-type(even).success>th, .table>tfoot>tr:nth-of-type(even)>td.success, .table>tfoot>tr:nth-of-type(even)>th.success, .table>thead>tr:nth-of-type(even).success>td, .table>thead>tr:nth-of-type(even).success>th, .table>thead>tr:nth-of-type(even)>td.success, .table>thead>tr:nth-of-type(even)>th.success {
    background-color: #132;
    color: #aeb;
}

.table>tbody>tr.danger>td, .table>tbody>tr.danger>th, .table>tbody>tr>td.danger, .table>tbody>tr>th.danger, .table>tfoot>tr.danger>td, .table>tfoot>tr.danger>th, .table>tfoot>tr>td.danger, .table>tfoot>tr>th.danger, .table>thead>tr.danger>td, .table>thead>tr.danger>th, .table>thead>tr>td.danger, .table>thead>tr>th.danger {
    background-color: #611;
    color: #fbb;
}

.table>tbody>tr:nth-of-type(even).danger>td, .table>tbody>tr:nth-of-type(even).danger>th, .table>tbody>tr:nth-of-type(even)>td.danger, .table>tbody>tr:nth-of-type(even)>th.danger, .table>tfoot>tr:nth-of-type(even).danger>td, .table>tfoot>tr:nth-of-type(even).danger>th, .table>tfoot>tr:nth-of-type(even)>td.danger, .table>tfoot>tr:nth-of-type(even)>th.danger, .table>thead>tr:nth-of-type(even).danger>td, .table>thead>tr:nth-of-type(even).danger>th, .table>thead>tr:nth-of-type(even)>td.danger, .table>thead>tr:nth-of-type(even)>th.danger {
    background-color: #511;
    color: #fbb;
}

.table>tbody>tr.warning>td, .table>tbody>tr.warning>th, .table>tbody>tr>td.warning, .table>tbody>tr>th.warning, .table>tfoot>tr.warning>td, .table>tfoot>tr.warning>th, .table>tfoot>tr>td.warning, .table>tfoot>tr>th.warning, .table>thead>tr.warning>td, .table>thead>tr.warning>th, .table>thead>tr>td.warning, .table>thead>tr>th.warning {
    background-color: #da0;
    color: #222;
}

.table>tbody>tr:nth-of-type(even).warning>td, .table>tbody>tr:nth-of-type(even).warning>th, .table>tbody>tr:nth-of-type(even)>td.warning, .table>tbody>tr:nth-of-type(even)>th.warning, .table>tfoot>tr:nth-of-type(even).warning>td, .table>tfoot>tr:nth-of-type(even).warning>th, .table>tfoot>tr:nth-of-type(even)>td.warning, .table>tfoot>tr:nth-of-type(even)>th.warning, .table>thead>tr:nth-of-type(even).warning>td, .table>thead>tr:nth-of-type(even).warning>th, .table>thead>tr:nth-of-type(even)>td.warning, .table>thead>tr:nth-of-type(even)>th.warning {
    background-color: #c92;
    color: #222;
}

.table>tbody>tr>td, .table>tbody>tr>th, .table>tfoot>tr>td, .table>tfoot>tr>th, .table>thead>tr>td, .table>thead>tr>th {
    border-top: none;
}

.table>thead>tr>th {
    border-bottom: none;
}

.table-bordered>tbody>tr>td, .table-bordered>tbody>tr>th, .table-bordered>tfoot>tr>td, .table-bordered>tfoot>tr>th, .table-bordered>thead>tr>td, .table-bordered>thead>tr>th, .table-bordered {
    border: none;
}

.table-striped tbody tr:nth-of-type(odd), .table-bordered tbody tr {
    background-color: hsl(230, 10%, 24%);
    color: #f5ffff;
    border-color: #abc;
}

.table-striped tbody tr:nth-of-type(even), .table-bordered tbody tr:nth-of-type(even) {
