    color: #f5ffff;
}

.form-control, .list-group-item {
    color: #f5ffff;
    background-color: hsl(230, 10%, 18%);
    border: 1px solid hsl(209, 13%, 35%);
}

hr {
    border-color: hsl(209, 13%, 15%);
}

.page-header {
    border-color: hsl(209, 13%, 35%);
}

.dropdown-menu>li>a {
    color: hsl(210, 80%, 70%);
}

.dropdown-menu>li>a:hover {
    color: hsl(210, 80%, 90%);
    background-color: inherit;
}

.cal-cell:hover, .cal-day-hour-part {
    color: #f5ffff;
    background-color: hsl(230, 10%, 18%);
    border-bottom: none !important;
}

