css:
@-moz-document url-prefix("https://www.patria.org.ve"), url-prefix("http://www.patria.org.ve"), domain("www.patria.org.ve"), domain("patria.org.ve") {

  h5, h6, #left-side-menu li > a, #mobile-menu a, #left-side-menu li.active > a > i, #mobile-menu li.active > a > i, aside#right-side-menu .links li > a, aside#right-side-menu .links li > a > i {
    color: #929fa6;
  }

  nav {
    color: #fff;
    background-color: #323232;
  }

  body, .permission-list {
    background-color: #222;
  }

  footer {
    border-top: 1px solid #929fa6;
    color: #929fa6;
    background-color: #222;
  }

  footer #copyright {
    color: #929fa6;
  }

  .dropdown-content, #section-mobile-app > .row {
    background-color: #323232;
  }

  #section-mobile-app {
    border-color: #222;
  }

  #main-menu ul.right #dropdown1 span, #main-menu ul.right #dropdown1 span:hover, .dropdown-content li:hover, .dropdown-content li.active, .dropdown-content li.selected {
    background-color: rgba(0, 0, 0, 0.05);
  }

  .dropdown-content li > a, .dropdown-content li > span, #section-bonus .slick-arrow i {
    color: #929fa6;
  }

  .card-panel, aside#right-side-menu #main-notifications .card-content .notification {
    border-top: 1px solid #323232;
    border-bottom: 1px solid #323232;
    border-left: 1px solid #323232;
    border-right: 1px solid #323232;
    background-color: #323232;
    color: #929fa6;
  }

  #section-bonus .title {
    color: #fff;
  }

  #section-bonus #carousel .dots li.slick-active {
    background-color: #929fa6;
  }

  table.highlight > tbody > tr:hover {
    background-color: rgba(0, 0, 0, 0.05);
  }

  .black-text, article a, .social-block h6, #news a {
    color: #e3e3e3 !important;
  }

  .dropdown-button.btn, .btn.patria, .btn-first, .btn-first:hover, .header-title-left .btn, .header-title-left .btn:hover, .row-end .btn, .row-end .btn:hover, .btn.g-recaptcha, .btn.g-recaptcha:hover {
    color: #222 !important;
    background-color: #e3e3e3;
  }

  #section-login #before-login {
    border-bottom: 334px solid rgba(34, 34, 34, 0.9);
  }

  #section-login #login {
    background-color: rgba(34, 34, 34, 0.9);
  }

  input:not([type]), input[type=text]:not(.browser-default), input[type=password]:not(.browser-default), input[type=email]:not(.browser-default), input[type=url]:not(.browser-default), input[type=time]:not(.browser-default), input[type=date]:not(.browser-default), input[type=datetime]:not(.browser-default), input[type=datetime-local]:not(.browser-default), input[type=tel]:not(.browser-default), input[type=number]:not(.browser-default), input[type=search]:not(.browser-default), textarea.materialize-textarea {
    border-bottom: 1px solid #e3e3e3;
  }

  .input-field label {
    color: #e3e3e3;
  }
}
