css:
@-moz-document domain(news.ycombinator.com) {
  /* @import url(https://fonts.googleapis.com/css?family=Open+Sans); */
  body,
  #hnmain {
      background-color: rgb(30, 30, 30);
      color: rgb(230, 230, 230);
  }
  a,
  .title a {
      color: rgb(200, 200, 200);
  }
  a:visited,
  .title a:visited {
      color: rgb(180, 180, 180);
  }
  .comment-tree tr.athing,
  .comment .c00 {
      background: rgb(60, 60, 60);
      color: rgb(210, 210, 210);
      font-size: 1.2em;
      line-height: 1.3em
  }
  .comment .c00 a {
      color: rgb(210, 210, 210);
  }
  .comment .c00 a:visited {
      color: rgb(180, 180, 180);
  }
  .title a {
      font-family: "Open Sans", "Verdana", sans-serif;
      font-size: 1.3em;
  }
  .subtext {
      font-size: 0.7em;
  }
  table.itemlist {
      width: 100%;
  }
  table.itemlist > tbody > tr.athing:nth-child(even),
  table.itemlist > tbody > tr.athing:nth-child(even) + tr {
      background-color: #555 !important;
  }
  table.itemlist > tbody > tr.athing:nth-child(even) .sitestr,
  table.itemlist > tbody > tr.athing:nth-child(even) + tr .subtext,
  table.itemlist > tbody > tr.athing:nth-child(even) + tr .subtext a,
  table.itemlist > tbody > tr.athing:nth-child(even) + tr .subtext a:visited {
      color: #111;
  }
  tr.spacer {
      height: 10px !important;
  }
  .athing .votearrow {
      background: none;
      width: 0;
      margin-top: 7px;
      height: 0;
      border-left: 5px solid transparent;
      border-right: 5px solid transparent;
      border-bottom: 5px solid #999;
  }
}
